import React, { useEffect, useState } from "react";
import AdminLayout from "../../layouts/AdminLayout";
import api from "../../api";

export default function AttendanceManagementAdmin() {
  const [list, setList] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [checked, setChecked] = useState([]);

  const load = async () => {
    try {
      const res = await api.get("/attendance");
      setList(res.data);

      const emp = await api.get("/employees");
      setEmployees(emp.data);
    } catch (err) {
      alert("Kh√¥ng t·∫£i ƒë∆∞·ª£c d·ªØ li·ªáu");
    }
  };

  useEffect(() => {
    load();
  }, []);

  const fmtTime = (t) =>
    t ? new Date(t).toLocaleTimeString("vi-VN", { hour12: false }) : "‚Äì";

  const fmtDate = (d) =>
    d ? new Date(d).toLocaleDateString("vi-VN") : "‚Äì";

  // ============================
  // Toggle ch·ªçn nhi·ªÅu nh√¢n vi√™n
  // ============================
  const toggleCheck = (uid) => {
    setChecked((prev) =>
      prev.includes(uid)
        ? prev.filter((x) => x !== uid)
        : [...prev, uid]
    );
  };

  // ============================
  // CH·∫§M C√îNG ƒê·ªíNG TH·ªúI
  // ============================
  const bulkCheckIn = async () => {
    if (checked.length === 0) {
      alert("Ch∆∞a ch·ªçn nh√¢n vi√™n");
      return;
    }

    if (!window.confirm(`Ch·∫•m c√¥ng ngay cho ${checked.length} nh√¢n vi√™n?`))
      return;

    try {
      await api.post("/attendance/bulk-checkin", {
        userIds: checked,
      });

      alert("‚úî ƒê√£ ch·∫•m c√¥ng th√†nh c√¥ng!");
      setChecked([]);
      load();
    } catch (err) {
      alert("L·ªói ch·∫•m c√¥ng nhanh");
    }
  };

  // ============================
  // X√ìA CH·∫§M C√îNG
  // ============================
  const remove = async (id) => {
    if (!window.confirm("Xo√° b·∫£n ghi n√†y?")) return;

    try {
      await api.delete(`/attendance/manual/${id}`);
      load();
    } catch (err) {
      alert("Kh√¥ng xo√° ƒë∆∞·ª£c");
    }
  };

  return (
    <AdminLayout>
      <h2 className="mb-3">‚è± Qu·∫£n l√Ω ch·∫•m c√¥ng</h2>

      {/* Ch·ªçn nhi·ªÅu ng∆∞·ªùi + ch·∫•m c√¥ng nhanh */}
      <div className="d-flex gap-2 mb-3">

        <button
          className="btn btn-success"
          disabled={checked.length === 0}
          onClick={bulkCheckIn}
        >
          ‚úî Ch·∫•m c√¥ng ngay ({checked.length})
        </button>

        <button
          className="btn btn-secondary"
          onClick={() => setChecked([])}
        >
          B·ªè ch·ªçn
        </button>
      </div>

      <div className="card p-3">
        <table className="table table-bordered table-striped">
          <thead>
            <tr>
              <th></th>
              <th>Nh√¢n vi√™n</th>
              <th>Ng√†y</th>
              <th>Gi·ªù v√†o</th>
              <th>Ra</th>
              <th>Tr·ªÖ (ph√∫t)</th>
              <th>OT (gi·ªù)</th>
              <th>C√¥ng</th>
              <th></th>
            </tr>
          </thead>

          <tbody>
            {employees.map((emp) => {
              // L·∫•y b·∫£n ghi h√¥m nay
              const rec = list.find(
                (x) => x.userId?._id === emp.userId?._id
              );

              return (
                <tr key={emp._id}>
                  {/* Checkbox ƒë·ªÉ ch·ªçn nhi·ªÅu */}
                  <td>
                    <input
                      type="checkbox"
                      checked={checked.includes(emp.userId?._id)}
                      onChange={() => toggleCheck(emp.userId?._id)}
                    />
                  </td>

                  <td>{emp.userId?.username}</td>

                  <td>{fmtDate(rec?.date)}</td>
                  <td>{fmtTime(rec?.checkIn)}</td>
                  <td>{fmtTime(rec?.checkOut)}</td>
                  <td>{rec?.lateMinutes ?? 0}</td>
                  <td>{rec?.overtimeHours ?? 0}</td>
                  <td>{rec?.totalDays ?? 0}</td>

                  <td className="text-nowrap">
                    {rec?._id && (
                      <button
                        className="btn btn-sm btn-danger"
                        onClick={() => remove(rec._id)}
                      >
                        üóë Xo√°
                      </button>
                    )}
                  </td>
                </tr>
              );
            })}
          </tbody>

        </table>
      </div>
    </AdminLayout>
  );
}
