import React, { useEffect, useState } from "react";
import api from "../../api";
import UserLayout from "../../layouts/UserLayout";
import { useAuth } from "../../context/AuthContext";
import ManagerAttendance from "./ManagerAttendance";

export default function Attendance() {
  const { user } = useAuth();

  // --- Load user tr∆∞·ªõc ---
  const role = user?.role;

  // --- D√π manager hay employee v·∫´n ph·∫£i khai b√°o hook ·ªü ƒë√¢y ---
  const [records, setRecords] = useState([]);
  const [loading, setLoading] = useState(true);

  // --- Ch·ªâ load ch·∫•m c√¥ng n·∫øu l√† employee ---
  useEffect(() => {
    if (role !== "manager") {
      loadAttendance();
    }
  }, [role]);

  const loadAttendance = async () => {
    try {
      const res = await api.get("/attendance");
      setRecords(res.data);
    } catch (err) {
      alert("Kh√¥ng t·∫£i ƒë∆∞·ª£c d·ªØ li·ªáu ch·∫•m c√¥ng");
    } finally {
      setLoading(false);
    }
  };

  const fmtDate = (d) =>
    d ? new Date(d).toLocaleDateString("vi-VN") : "‚Äì";

  const fmtTime = (t) =>
    t ? new Date(t).toLocaleTimeString("vi-VN", { hour12: false }) : "‚Äì";

  // ---------- RENDER ----------
  // N·∫øu l√† Manager ‚Üí render trang ManagerAttendance
  if (role === "manager") {
    return <ManagerAttendance />;
  }

  // N·∫øu l√† Employee ‚Üí render b·∫£ng l·ªãch s·ª≠
  return (
    <UserLayout>
      <h2 className="mb-3">üïí L·ªãch s·ª≠ ch·∫•m c√¥ng</h2>

      <div className="card p-3">
        {loading ? (
          <p>ƒêang t·∫£i...</p>
        ) : records.length === 0 ? (
          <p>Kh√¥ng c√≥ d·ªØ li·ªáu.</p>
        ) : (
          <table className="table table-bordered table-striped">
            <thead>
              <tr>
                <th>Ng√†y</th>
                <th>Gi·ªù v√†o</th>
                <th>Gi·ªù ra</th>
                <th>ƒêi tr·ªÖ (ph√∫t)</th>
                <th>TƒÉng ca (gi·ªù)</th>
                <th>Ng√†y c√¥ng</th>
              </tr>
            </thead>
            <tbody>
              {records.map((r) => (
                <tr key={r._id}>
                  <td>{fmtDate(r.date)}</td>
                  <td>{fmtTime(r.checkIn)}</td>
                  <td>{fmtTime(r.checkOut)}</td>
                  <td>{r.lateMinutes || 0}</td>
                  <td>{r.overtimeHours?.toFixed(2) || 0}</td>
                  <td>{r.totalDays || 0}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </UserLayout>
  );
}
