// frontend/src/pages/user/Chat.js
import React, { useEffect, useMemo, useRef, useState } from "react";
import io from "socket.io-client";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";
import { useAuth } from "../../context/AuthContext";

const socket = io(process.env.REACT_APP_API_BASE?.replace("/api","") || "http://localhost:5000", {
  transports: ["websocket"]
});

export default function Chat() {
  const { user } = useAuth(); // { token, user: {_id, username, role, ...} }
  const me = user?.user;
  const [deptRoom, setDeptRoom] = useState(null);
  const [privateRooms, setPrivateRooms] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [chatOutsideDept, setChatOutsideDept] = useState(false);
  const [currentRoom, setCurrentRoom] = useState(null); // { _id, type, name, department, participants }
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");

  const bottomRef = useRef();

  const myDepartment = useMemo(() => me?.department || null, [me]);

  useEffect(() => {
    // join socket rooms
    socket.emit("join", { userId: me?._id, department: me?.department });
  }, [me]);

  const loadRooms = async () => {
    const res = await api.get("/messages/rooms");
    setPrivateRooms(res.data.privateRooms || []);
    setDeptRoom(res.data.deptRoom || null);
  };

  const loadEmployees = async () => {
    // b·∫°n c√≥ API li·ªát k√™ t·∫•t c·∫£ nh√¢n vi√™n
    const res = await api.get("/admin/employees");
    setEmployees(res.data || []);
  };

  const loadMessages = async (room) => {
    const res = await api.get(`/messages/${room._id}`);
    setMessages(res.data || []);
    setTimeout(() => bottomRef.current?.scrollIntoView({behavior:"smooth"}), 50);
  };

  useEffect(() => { loadRooms(); loadEmployees(); }, []);

  useEffect(() => {
    const handler = (payload) => {
      // N·∫øu payload kh√¥ng thu·ªôc current room th√¨ b·ªè qua
      if (!currentRoom) return;
      if (payload.roomId && payload.roomId !== currentRoom._id) return;
      setMessages((prev) => [...prev, {
        _id: Math.random().toString(36).slice(2),
        sender: { username: payload.fromUsername || "N/A" },
        content: payload.message,
        createdAt: new Date().toISOString()
      }]);
      bottomRef.current?.scrollIntoView({behavior:"smooth"});
    };
    socket.on("receive_message", handler);
    return () => socket.off("receive_message", handler);
  }, [currentRoom]);

  const openRoom = async (room) => {
    setCurrentRoom(room);
    await loadMessages(room);
  };

  const startPrivateWith = async (userId) => {
    const res = await api.post("/messages/rooms/private", { otherUserId: userId });
    await loadRooms();
    setCurrentRoom(res.data);
    await loadMessages(res.data);
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!text.trim() || !currentRoom) return;

    // L∆∞u DB
    const saved = await api.post("/messages", { roomId: currentRoom._id, content: text.trim() });

    // Emit Realtime theo lo·∫°i
    if (currentRoom.type === "group") {
      socket.emit("send_message", {
        type: "group",
        department: currentRoom.department,
        roomId: currentRoom._id,
        fromUserId: me?._id,
        fromUsername: me?.username,
        message: saved.data.content
      });
    } else {
      // t√¨m ng∆∞·ªùi c√≤n l·∫°i
      const others = (currentRoom.participants || []).map(p => p._id).filter(id => id !== me?._id);
      const toUserId = others?.[0];
      socket.emit("send_message", {
        type: "private",
        toUserId,
        roomId: currentRoom._id,
        fromUserId: me?._id,
        fromUsername: me?.username,
        message: saved.data.content
      });
    }

    setMessages((prev) => [...prev, saved.data]);
    setText("");
    bottomRef.current?.scrollIntoView({behavior:"smooth"});
  };

  return (
    <div className="container-fluid mt-3">
      <div className="row">
        <div className="col-3">
          <SidebarMenu role="user" />
          <div className="card mt-3">
            <div className="card-header"><strong>Chat</strong></div>
            <div className="card-body p-2">
              <div className="form-check form-switch mb-2">
                <input className="form-check-input" type="checkbox"
                  checked={chatOutsideDept} onChange={() => setChatOutsideDept(v => !v)} id="switchOutside" />
                <label htmlFor="switchOutside">Chat ngo√†i ph√≤ng ban</label>
              </div>

              {/* Nh√≥m ph√≤ng ban */}
              {deptRoom && (
                <div className="mb-3">
                  <div className="d-flex justify-content-between align-items-center">
                    <strong>üë• {deptRoom.name}</strong>
                    <button className="btn btn-sm btn-outline-primary"
                      onClick={() => openRoom(deptRoom)}>M·ªü</button>
                  </div>
                </div>
              )}

              {/* Danh s√°ch nh√¢n vi√™n */}
              <hr />
              <small className="text-muted">Nh√¢n vi√™n {chatOutsideDept ? "to√†n c√¥ng ty" : "trong ph√≤ng ban"}</small>
              <div className="list-group mt-1" style={{ maxHeight: 320, overflowY: "auto" }}>
                {employees
                  .filter(e => e.userId?._id !== me?._id)
                  .filter(e => chatOutsideDept ? true : e.department === myDepartment)
                  .map(e => (
                    <button key={e._id} className="list-group-item list-group-item-action"
                      onClick={() => startPrivateWith(e.userId?._id)}>
                      {e.name} <span className="text-muted">({e.userId?.username})</span>
                    </button>
                ))}
              </div>

              {/* Private rooms ƒë√£ c√≥ */}
              <hr className="my-2"/>
              <small className="text-muted">ƒêo·∫°n chat g·∫ßn ƒë√¢y</small>
              <div className="list-group mt-1" style={{ maxHeight: 200, overflowY: "auto" }}>
                {privateRooms.map(r => (
                  <button key={r._id} className="list-group-item list-group-item-action"
                    onClick={() => openRoom(r)}>
                    {r.participants?.map(p => p.username).join(" , ")}
                  </button>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* Chat box */}
        <div className="col-9">
          <div className="card">
            <div className="card-header">
              {currentRoom
                ? (currentRoom.type === "group" ? `üë• ${currentRoom.name}` :
                  `üí¨ ${(currentRoom.participants || []).map(p => p.username).join(" , ")}`)
                : "Ch·ªçn 1 ƒëo·∫°n h·ªôi tho·∫°i"
              }
            </div>
            <div className="card-body" style={{ height: 520, overflowY: "auto" }}>
              {messages.map(m => (
                <div key={m._id} className={`mb-2 ${m.sender?._id === me?._id ? "text-end" : ""}`}>
                  <div><small className="text-muted">{m.sender?.username} ¬∑ {new Date(m.createdAt).toLocaleString()}</small></div>
                  <div className={`d-inline-block p-2 rounded ${m.sender?._id === me?._id ? "bg-primary text-white" : "bg-light"}`}>
                    {m.content}
                  </div>
                </div>
              ))}
              <div ref={bottomRef}/>
            </div>
            <div className="card-footer">
              <form onSubmit={sendMessage} className="d-flex gap-2">
                <input className="form-control" placeholder="Nh·∫≠p tin nh·∫Øn..." value={text} onChange={(e)=>setText(e.target.value)} />
                <button className="btn btn-primary" disabled={!currentRoom || !text.trim()}>G·ª≠i</button>
              </form>
            </div>
          </div>
