import React, { useState, useEffect } from "react";
import axios from "axios";

function ProfileUpdate() {
  const [profile, setProfile] = useState({});
  const token = localStorage.getItem("token");

  useEffect(() => {
    axios.get("http://localhost:5000/api/employees/me", {
      headers: { Authorization: `Bearer ${token}` }
    }).then(res => setProfile(res.data));
  }, []);

  const handleChange = e => setProfile({ ...profile, [e.target.name]: e.target.value });

  const handleSave = async () => {
    await axios.put("http://localhost:5000/api/employees/me", profile, {
      headers: { Authorization: `Bearer ${token}` }
    });
    alert("Cập nhật thành công!");
  };

  return (
    <div className="container">
      <h3>Cập nhật thông tin cá nhân</h3>
      <input className="form-control mb-2" name="name" value={profile.name || ""} onChange={handleChange}/>
      <input className="form-control mb-2" name="department" value={profile.department || ""} onChange={handleChange}/>
      <input className="form-control mb-2" name="position" value={profile.position || ""} onChange={handleChange}/>
      <button className="btn btn-primary" onClick={handleSave}>Lưu</button>
    </div>
  );
}

export default ProfileUpdate;
