import React, { useEffect, useState } from "react";
import SidebarMenu from "../../components/SidebarMenu";
import api from "../../api";

function HRManagement() {
  const [list, setList] = useState([]);
  const [form, setForm] = useState({ username: "", password: "", name: "", department: "", position: "", role: "employee" });

  const load = async () => {
    const res = await api.get("/admin/employees");
    setList(res.data);
  };
  useEffect(() => { load(); }, []);

  const onSubmit = async (e) => {
    e.preventDefault();
    await api.post("/admin/employees", form);
    setForm({ username: "", password: "", name: "", department: "", position: "", role: "employee" });
    load();
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="admin" /></div>
        <div className="col-9">
          <h3>👨‍💼 Quản lý nhân sự</h3>

          <form onSubmit={onSubmit} className="mb-3">
            <div className="row g-2">
              <div className="col"><input className="form-control" placeholder="Username" value={form.username} onChange={e=>setForm({...form,username:e.target.value})} /></div>
              <div className="col"><input className="form-control" placeholder="Password" value={form.password} onChange={e=>setForm({...form,password:e.target.value})} /></div>
            </div>
            <div className="row g-2 mt-2">
              <div className="col"><input className="form-control" placeholder="Họ tên" value={form.name} onChange={e=>setForm({...form,name:e.target.value})} /></div>
              <div className="col"><input className="form-control" placeholder="Phòng ban" value={form.department} onChange={e=>setForm({...form,department:e.target.value})} /></div>
              <div className="col"><input className="form-control" placeholder="Chức vụ" value={form.position} onChange={e=>setForm({...form,position:e.target.value})} /></div>
            </div>
            <div className="row g-2 mt-2">
              <div className="col">
                <select className="form-control" value={form.role} onChange={(e)=>setForm({...form,role:e.target.value})}>
                  <option value="employee">Nhân viên</option>
                  <option value="manager">Quản lý</option>
                  <option value="admin">Admin</option>
                </select>
              </div>
              <div className="col"><button className="btn btn-primary w-100">Thêm nhân viên</button></div>
            </div>
          </form>

          <table className="table table-bordered">
            <thead><tr><th>Username</th><th>Role</th><th>Họ tên</th><th>Phòng ban</th><th>Chức vụ</th></tr></thead>
            <tbody>
              {list.map(e => (
                <tr key={e._id}>
                  <td>{e.userId?.username}</td>
                  <td>{e.userId?.role}</td>
                  <td>{e.name}</td>
                  <td>{e.department}</td>
                  <td>{e.position}</td>
                </tr>
              ))}
            </tbody>
          </table>

        </div>
      </div>
    </div>
  );
}

export default HRManagement;
