// src/pages/user/LeaveHistory.js
import React, { useEffect, useState } from "react";
import api from "../../api";
import UserLayout from "../../layouts/UserLayout";
import { useAuth } from "../../context/AuthContext";

export default function LeaveHistory() {
  const { user } = useAuth(); // üî• L·∫§Y ROLE CHU·∫®N
  const [list, setList] = useState([]);
  const [loading, setLoading] = useState(true);

  // N·∫øu user ch∆∞a load ‚Üí tr√°nh vƒÉng login
  if (!user) {
    return (
      <UserLayout>
        <div className="text-center p-3">ƒêang t·∫£i d·ªØ li·ªáu...</div>
      </UserLayout>
    );
  }

  useEffect(() => {
    const load = async () => {
      try {
        const res = await api.get("/leave-requests/me");
        setList(res.data || []);
      } catch (err) {
        console.error("‚ùå L·ªói load l·ªãch s·ª≠ ngh·ªâ ph√©p:", err);
      } finally {
        setLoading(false);
      }
    };

    load();
  }, []);

  const fmt = (d) => new Date(d).toLocaleDateString("vi-VN");

  const statusBadge = (st) => {
    switch (st) {
      case "approved":
        return <span className="badge bg-success">ƒê√£ duy·ªát</span>;
      case "rejected":
        return <span className="badge bg-danger">T·ª´ ch·ªëi</span>;
      default:
        return <span className="badge bg-warning text-dark">Ch·ªù duy·ªát</span>;
    }
  };

  return (
    <UserLayout role={user.role}>
      <h2 className="mb-3">üìú L·ªãch s·ª≠ ngh·ªâ ph√©p</h2>

      <div className="card p-3">
        {loading ? (
          <p className="text-muted">‚è≥ ƒêang t·∫£i d·ªØ li·ªáu...</p>
        ) : list.length === 0 ? (
          <p className="text-muted">Kh√¥ng c√≥ l·ªãch s·ª≠ ngh·ªâ ph√©p.</p>
        ) : (
          <div className="table-responsive">
            <table className="table table-bordered table-striped align-middle">
              <thead className="table-light">
                <tr>
                  <th>T·ª´ ng√†y</th>
                  <th>ƒê·∫øn ng√†y</th>
                  <th>L√Ω do</th>
                  <th>Tr·∫°ng th√°i</th>
                </tr>
              </thead>
              <tbody>
                {list.map((l) => (
                  <tr key={l._id}>
                    <td>{fmt(l.from)}</td>
                    <td>{fmt(l.to)}</td>
                    <td>{l.reason}</td>
                    <td>{statusBadge(l.status)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </UserLayout>
  );
}
