                      {/* ‚úÖ Ch·ªâ hi·ªÉn th·ªã t√™n v√† username, kh√¥ng hi·ªÉn th·ªã ID */}
                      {r.name} {r.username && `(${r.username})`}
                    </td>
                    <td>{r.department}</td>
                    <td>{r.position}</td>
                    <td>{r.totalHours?.toFixed?.(2) || 0}</td>
                    <td>{r.totalLeaves || 0}</td>
                    <td>{r.avgScore}</td>
                  </tr>
