import React, { useEffect, useState } from "react";
import api from "../../api";
import UserLayout from "../../layouts/UserLayout";

function Attendance() {
  const [records, setRecords] = useState([]);
  const [todayRecord, setTodayRecord] = useState(null);
  const [loading, setLoading] = useState(true);

  const load = async () => {
    try {
      const res = await api.get("/attendance");
      setRecords(res.data);

      const today = new Date().toISOString().split("T")[0];
      const todayRec = res.data.find((r) => r.date === today);

      setTodayRecord(todayRec || null);
    } catch (err) {
      console.error("‚ùå L·ªói load ch·∫•m c√¥ng:", err);
      alert("Kh√¥ng t·∫£i ƒë∆∞·ª£c d·ªØ li·ªáu ch·∫•m c√¥ng!");
    } finally {
      setLoading(false);
    }
  };

  const checkIn = async () => {
    try {
      await api.post("/attendance/check-in");
      alert("‚úÖ Check-in th√†nh c√¥ng (Gi·ªù ra m·∫∑c ƒë·ªãnh 17:00)");
      load();
    } catch (err) {
      alert(err.response?.data?.error || "‚ùå L·ªói check-in");
    }
  };

  const startOvertime = async () => {
    try {
      await api.post("/attendance/overtime");
      alert("‚è∞ B·∫Øt ƒë·∫ßu tƒÉng ca");
      load();
    } catch (err) {
      alert(err.response?.data?.error || "‚ùå L·ªói tƒÉng ca");
    }
  };

  const endOvertime = async () => {
    try {
      await api.post("/attendance/overtime/checkout");
      alert("‚èπ K·∫øt th√∫c tƒÉng ca");
      load();
    } catch (err) {
      alert(err.response?.data?.error || "‚ùå L·ªói k·∫øt th√∫c tƒÉng ca");
    }
  };

  useEffect(() => {
    load();
  }, []);

  const fmtTime = (t) =>
    t ? new Date(t).toLocaleTimeString("vi-VN", { hour12: false }) : "‚Äì";

  return (
    <UserLayout>
      <h3>üïí Ch·∫•m c√¥ng</h3>

      {/* H√¥m nay */}
      {todayRecord ? (
        <div className="card p-3 mb-3 bg-light border">
          <h5 className="mb-2">üìÖ H√¥m nay: {todayRecord.date}</h5>

          <div>
            ‚úîÔ∏è Check-in: <strong>{fmtTime(todayRecord.checkIn)}</strong>
          </div>
          <div>
            üïì Check-out:{" "}
            <strong>
              {todayRecord.checkOut ? fmtTime(todayRecord.checkOut) : "17:00"}
            </strong>
          </div>
          <div>‚è≥ ƒêi tr·ªÖ: {todayRecord.lateMinutes || 0} ph√∫t</div>
          <div>‚è∞ TƒÉng ca: {todayRecord.overtimeHours?.toFixed(2) || 0} gi·ªù</div>
          <div>üìÜ Ng√†y c√¥ng: {todayRecord.totalDays || 0}</div>
        </div>
      ) : (
        <div className="alert alert-warning">üìå B·∫°n ch∆∞a check-in h√¥m nay.</div>
      )}

      {/* Buttons */}
      <div className="mb-3 d-flex gap-2">
        <button
          className="btn btn-success"
          onClick={checkIn}
          disabled={!!todayRecord?.checkIn}
        >
          ‚úÖ Check-in
        </button>

        <button
          className="btn btn-warning"
          onClick={startOvertime}
          disabled={!todayRecord?.checkIn || todayRecord?.overtimeStart}
        >
          ‚è∞ B·∫Øt ƒë·∫ßu tƒÉng ca
        </button>

        <button
          className="btn btn-danger"
          onClick={endOvertime}
          disabled={!todayRecord?.overtimeStart}
        >
          ‚èπ K·∫øt th√∫c tƒÉng ca
        </button>
      </div>

      {/* B·∫£ng l·ªãch s·ª≠ */}
      <div className="card p-3">
        <h5>L·ªãch s·ª≠ ch·∫•m c√¥ng</h5>

        {loading ? (
          <p>‚è≥ ƒêang t·∫£i...</p>
        ) : records.length === 0 ? (
          <p>Kh√¥ng c√≥ d·ªØ li·ªáu ch·∫•m c√¥ng.</p>
        ) : (
          <div className="table-responsive">
            <table className="table table-striped table-bordered mt-2">
              <thead>
                <tr>
                  <th>Ng√†y</th>
                  <th>Gi·ªù v√†o</th>
                  <th>Gi·ªù ra</th>
                  <th>ƒêi tr·ªÖ (ph√∫t)</th>
                  <th>TƒÉng ca (gi·ªù)</th>
                  <th>Ng√†y c√¥ng</th>
                </tr>
              </thead>
              <tbody>
                {records.map((r) => (
                  <tr key={r._id}>
                    <td>{r.date}</td>
                    <td>{fmtTime(r.checkIn)}</td>
                    <td>{r.checkOut ? fmtTime(r.checkOut) : "17:00"}</td>
                    <td>{r.lateMinutes || 0}</td>
                    <td>{r.overtimeHours?.toFixed(2) || 0}</td>
                    <td>{r.totalDays || 0}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </UserLayout>
  );
}

export default Attendance;
