import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

function WorkScheduleAdmin() {
  const [list, setList] = useState([]);
  const [form, setForm] = useState({ title: "", description: "", department: "", assignedTo: "", date: "" });
  const [employees, setEmployees] = useState([]);

  const load = async () => {
    const res = await api.get("/admin/work-schedule");
    setList(res.data);
    const emp = await api.get("/admin/employees");
    setEmployees(emp.data);
  };

  useEffect(() => { load(); }, []);

  const addSchedule = async () => {
    try {
      await api.post("/admin/work-schedule", form);
      alert("‚úÖ Th√™m l·ªãch th√†nh c√¥ng");
      setForm({ title: "", description: "", department: "", assignedTo: "", date: "" });
      load();
    } catch (err) {
      alert("‚ùå L·ªói khi th√™m l·ªãch");
    }
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="admin" /></div>
        <div className="col-9">
          <h3>üìÖ Qu·∫£n l√Ω l·ªãch l√†m vi·ªác</h3>

          <div className="card p-3 mb-3">
            <h5>‚ûï Th√™m l·ªãch m·ªõi</h5>
            <input className="form-control mb-2" placeholder="Ti√™u ƒë·ªÅ"
              value={form.title} onChange={(e) => setForm({ ...form, title: e.target.value })} />
            <textarea className="form-control mb-2" placeholder="M√¥ t·∫£"
              value={form.description} onChange={(e) => setForm({ ...form, description: e.target.value })} />

            <select className="form-control mb-2"
              value={form.department}
              onChange={(e) => setForm({ ...form, department: e.target.value, assignedTo: "" })}>
              <option value="">-- G√°n cho ph√≤ng ban --</option>
              <option value="Kinh doanh">Kinh doanh</option>
              <option value="K·ªπ thu·∫≠t">K·ªπ thu·∫≠t</option>
              <option value="Nh√¢n s·ª±">Nh√¢n s·ª±</option>
            </select>

            <select className="form-control mb-2"
              value={form.assignedTo}
              onChange={(e) => setForm({ ...form, assignedTo: e.target.value, department: "" })}>
              <option value="">-- G√°n cho nh√¢n vi√™n --</option>
              {employees.map(emp => (
                <option key={emp._id} value={emp.userId?._id}>{emp.name} ({emp.userId?.username})</option>
              ))}
            </select>

            <input type="date" className="form-control mb-2"
              value={form.date} onChange={(e) => setForm({ ...form, date: e.target.value })} />

            <button className="btn btn-primary" onClick={addSchedule}>Th√™m l·ªãch</button>
          </div>

          <table className="table table-bordered">
            <thead>
              <tr><th>Ti√™u ƒë·ªÅ</th><th>M√¥ t·∫£</th><th>Ph√≤ng ban</th><th>Nh√¢n vi√™n</th><th>Ng√†y</th></tr>
            </thead>
            <tbody>
              {list.map(s => (
                <tr key={s._id}>
                  <td>{s.title}</td>
                  <td>{s.description}</td>
                  <td>{s.department || "-"}</td>
                  <td>{s.assignedTo?.username || "-"}</td>
                  <td>{new Date(s.date).toLocaleDateString("vi-VN")}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
export default WorkScheduleAdmin;
