// frontend/src/pages/user/NotificationsUser.js
import React, { useEffect, useState } from "react";
import SidebarMenu from "../../components/SidebarMenu";
import api from "../../api";

function NotificationsUser() {
  const [list, setList] = useState([]);

  useEffect(() => {
    const load = async () => {
      try {
        const res = await api.get("/admin/notifications"); // user ch·ªâ xem
        setList(res.data);
      } catch (err) {
        console.error(err);
        alert("‚ùå L·ªói t·∫£i th√¥ng b√°o");
      }
    };
    load();
  }, []);

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="employee" /></div>
        <div className="col-9">
          <h3>üîî Th√¥ng b√°o</h3>
          <ul className="list-group">
            {list.map((n) => (
              <li key={n._id} className="list-group-item">
                <strong>{n.title}</strong> - {n.message}
                <span className="text-muted"> ({new Date(n.createdAt).toLocaleString()})</span>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default NotificationsUser;
