// frontend/src/pages/auth/Login.js (ho·∫∑c n∆°i x·ª≠ l√Ω login)
import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Login() {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/api/auth/login", {
        username,
        password,
      });

      // ‚úÖ L∆∞u v√†o localStorage theo ƒë·ªãnh d·∫°ng chu·∫©n
      localStorage.setItem(
        "authUser",
        JSON.stringify({
          token: res.data.token,
          user: res.data.user, // bao g·ªìm id, username, role
        })
      );

      // üëâ ƒêi·ªÅu h∆∞·ªõng t√πy role
      if (res.data.user.role === "admin") {
        navigate("/admin/dashboard");
      } else {
        navigate("/user/dashboard");
      }

    } catch (err) {
      console.error("‚ùå L·ªói ƒëƒÉng nh·∫≠p:", err);
      alert("Sai t√™n ƒëƒÉng nh·∫≠p ho·∫∑c m·∫≠t kh·∫©u!");
    }
  };

  return (
    <div className="container mt-5" style={{ maxWidth: 400 }}>
      <h3>üîê ƒêƒÉng nh·∫≠p</h3>
      <form onSubmit={handleLogin}>
        <div className="mb-3">
          <label>T√™n ƒëƒÉng nh·∫≠p</label>
          <input
            type="text"
            className="form-control"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label>M·∫≠t kh·∫©u</label>
          <input
            type="password"
            className="form-control"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit" className="btn btn-primary w-100">
          ƒêƒÉng nh·∫≠p
        </button>
      </form>
    </div>
  );
}

export default Login;
