// frontend/src/pages/admin/WorkScheduleAdmin.js
import React, { useEffect, useState } from "react";
import SidebarMenu from "../../components/SidebarMenu";
import api from "../../api";

function WorkScheduleAdmin() {
  const [list, setList] = useState([]);
  const [form, setForm] = useState({ title: "", assignedTo: "", date: "" });

  const load = async () => {
    try {
      const res = await api.get("/admin/work-schedule");
      setList(res.data);
    } catch (err) {
      console.error(err);
      alert("‚ùå L·ªói t·∫£i l·ªãch l√†m vi·ªác");
    }
  };

  useEffect(() => { load(); }, []);

  const add = async () => {
    try {
      await api.post("/admin/work-schedule", form);
      setForm({ title: "", assignedTo: "", date: "" });
      load();
      alert("‚úÖ Th√™m l·ªãch l√†m vi·ªác th√†nh c√¥ng");
    } catch (err) {
      console.error(err);
      alert("‚ùå L·ªói th√™m l·ªãch");
    }
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="admin" /></div>
        <div className="col-9">
          <h3>üìÖ L·ªãch l√†m vi·ªác (Admin)</h3>
          <input
            className="form-control mb-2"
            placeholder="T√™n c√¥ng vi·ªác"
            value={form.title}
            onChange={(e) => setForm({ ...form, title: e.target.value })}
          />
          <input
            className="form-control mb-2"
            placeholder="UserId"
            value={form.assignedTo}
            onChange={(e) => setForm({ ...form, assignedTo: e.target.value })}
          />
          <input
            className="form-control mb-2"
            type="date"
            value={form.date}
            onChange={(e) => setForm({ ...form, date: e.target.value })}
          />
          <button className="btn btn-primary" onClick={add}>Th√™m</button>

          <ul className="list-group mt-3">
            {list.map((s) => (
              <li key={s._id} className="list-group-item">
                {s.title} - {s.assignedTo?.username} ({new Date(s.date).toLocaleDateString()})
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default WorkScheduleAdmin;
