// frontend/src/pages/user/NotificationsUser.js
import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

export default function NotificationsUser(){
  const [list, setList] = useState([]);

  const load = async () => {
    // g·ª£i √Ω: tr·∫£ v·ªÅ th√¥ng b√°o target: all | department:xxx | user:me
    const res = await api.get("/notifications"); // N·∫øu hi·ªán t·∫°i route trong adminRoutes l√† /api/admin/notifications, d√πng "/admin/notifications"
    setList(res.data || []);
  };

  useEffect(()=>{ load(); },[]);

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="user" /></div>
        <div className="col-9">
          <h3>üîî Th√¥ng b√°o</h3>
          <ul className="list-group">
            {list.map(n => (
              <li className="list-group-item" key={n._id}>
                <strong>{n.title}</strong>
                <div className="text-muted">{new Date(n.createdAt).toLocaleString()}</div>
                <div>{n.message}</div>
              </li>
            ))}
            {list.length === 0 && (
              <li className="list-group-item text-center text-muted">Ch∆∞a c√≥ th√¥ng b√°o</li>
            )}
          </ul>
        </div>
      </div>
    </div>
  );
}
