import React, { useEffect, useState } from "react";
import axios from "axios";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

export default function SalaryManagementAdmin() {
  const [salaries, setSalaries] = useState([]);
  const [editRow, setEditRow] = useState(null);

  // 🧭 Load danh sách lương từ server
  const loadData = async () => {
    try {
      const res = await axios.get(`${API_URL}/admin/salary`);
      console.log("📊 Salary data from server:", res.data);
      setSalaries(res.data);
    } catch (err) {
      console.error("❌ Lỗi tải bảng lương:", err);
    }
  };

  useEffect(() => {
    loadData();
  }, []);

  // 📝 Bắt đầu chỉnh sửa 1 dòng
  const handleEdit = (row) => {
    setEditRow({ ...row });
  };

  // ⏳ Hủy chỉnh sửa
  const cancelEdit = () => {
    setEditRow(null);
  };

  // 📝 Cập nhật giá trị trong ô input khi chỉnh sửa
  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditRow((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // ✅ Lưu chỉnh sửa
  const saveEdit = async () => {
    try {
      const res = await axios.put(`${API_URL}/admin/salary/${editRow._id}`, {
        dailyRate: editRow.dailyRate,
        overtimeRate: editRow.overtimeRate,
      });

      console.log("📥 Server trả về:", res.data);

      // 👉 Cập nhật state trực tiếp (không cần refetch toàn bộ)
      const updated = res.data.salary;
      setSalaries((prev) =>
        prev.map((item) => (item._id === updated._id ? { ...item, ...updated } : item))
      );

      setEditRow(null);
    } catch (err) {
      console.error("❌ Update Error:", err);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>💰 Quản lý bảng lương</h2>

      <table border="1" cellPadding="8" cellSpacing="0" width="100%">
        <thead>
          <tr>
            <th>Tên nhân viên</th>
            <th>Lương ngày (VNĐ)</th>
            <th>Tăng ca (VNĐ/h)</th>
            <th>Số ngày công</th>
            <th>Phạt</th>
            <th>Tăng ca</th>
            <th>Tổng lương</th>
            <th>Thao tác</th>
          </tr>
        </thead>
        <tbody>
          {salaries.map((row) => (
            <tr key={row._id}>
              <td>{row.name}</td>
              <td>
                {editRow && editRow._id === row._id ? (
                  <input
                    type="number"
                    name="dailyRate"
                    value={editRow.dailyRate}
                    onChange={handleChange}
                  />
                ) : (
                  row.dailyRate.toLocaleString()
                )}
              </td>
              <td>
                {editRow && editRow._id === row._id ? (
                  <input
                    type="number"
                    name="overtimeRate"
                    value={editRow.overtimeRate}
                    onChange={handleChange}
                  />
                ) : (
                  row.overtimeRate.toLocaleString()
                )}
              </td>
              <td>{row.totalDays}</td>
              <td>{row.penalty?.toLocaleString()}</td>
              <td>{row.overtimePay?.toLocaleString()}</td>
              <td>{row.amount?.toLocaleString()}</td>
              <td>
                {editRow && editRow._id === row._id ? (
                  <>
                    <button onClick={saveEdit}>💾 Lưu</button>
                    <button onClick={cancelEdit} style={{ marginLeft: "5px" }}>
                      ❌ Hủy
                    </button>
                  </>
                ) : (
                  <button onClick={() => handleEdit(row)}>✏️ Sửa</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
