        !window.confirm(
          "B·∫°n ƒëang xem ng√†y kh√°c h√¥m nay. Ch·∫•m c√¥ng nhanh lu√¥n t√≠nh cho NG√ÄY H√îM NAY. Ti·∫øp t·ª•c?"
        )
      )
        return;
    }

    if (!window.confirm(`Ch·∫•m c√¥ng ngay cho ${checked.length} nh√¢n vi√™n?`))
      return;

    try {
      await api.post("/attendance/bulk-checkin", { userIds: checked });

      alert("‚úî ƒê√£ ch·∫•m c√¥ng!");
      setChecked([]);
