              <th>Nh√¢n vi√™n</th>
              <th>Ng√†y</th>
              <th>Gi·ªù v√†o</th>
              <th>Ra</th>
              <th>Tr·ªÖ (ph√∫t)</th>
              <th>OT (gi·ªù)</th>
              <th>C√¥ng</th>
              <th></th>
            </tr>
          </thead>
          <tbody>{renderBody()}</tbody>
        </table>
      </div>

      {/* POPUP EDIT */}
      {editing && (
        <div
          style={{
            position: "fixed",
            inset: 0,
            background: "rgba(0,0,0,0.4)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 9999,
          }}
        >
          <div
            className="card p-4"
            style={{ width: 420, background: "white" }}
          >
            <h4 className="mb-3">‚úèÔ∏è S·ª≠a ch·∫•m c√¥ng</h4>

            <label>Gi·ªù v√†o</label>
            <input
              type="datetime-local"
              className="form-control mb-2"
              value={editData.checkIn}
              onChange={(e) => setEditData({ ...editData, checkIn: e.target.value })}
            />

            <label>Gi·ªù ra</label>
            <input
              type="datetime-local"
              className="form-control mb-2"
              value={editData.checkOut}
              onChange={(e) =>
                setEditData({ ...editData, checkOut: e.target.value })
              }
            />

            <label>ƒêi tr·ªÖ (ph√∫t)</label>
            <input
              type="number"
              className="form-control mb-2"
              value={editData.lateMinutes}
