import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

function SalaryManagementAdmin() {
  const [salaries, setSalaries] = useState([]);
  const [editId, setEditId] = useState(null);
  const [form, setForm] = useState({ dailyRate: "", overtimeRate: "" });

  const load = async () => {
    try {
      // ‚úÖ ƒê√∫ng endpoint theo server.js
      const res = await api.get("/admin/salary");
      const sanitized = res.data.map((s) => ({
        ...s,
        dailyRate: s.dailyRate ?? 0,
        overtimeRate: s.overtimeRate ?? 0,
        penalty: s.penalty ?? 0,
        amount: s.amount ?? 0,
        totalDays: s.totalDays ?? 0,
        overtimeHours: s.overtimeHours ?? 0,
      }));
      setSalaries(sanitized);
    } catch (err) {
      console.error("‚ùå API Error:", err.response?.data || err.message);
      alert("‚ùå Kh√¥ng t·∫£i ƒë∆∞·ª£c d·ªØ li·ªáu l∆∞∆°ng");
    }
  };

  useEffect(() => {
    load();
  }, []);

  const startEdit = (s) => {
    setEditId(s._id);
    setForm({
      dailyRate: s.dailyRate ?? 0,
      overtimeRate: s.overtimeRate ?? 0,
    });
  };

  const saveEdit = async (id) => {
    try {
      // ‚úÖ ƒê√∫ng endpoint theo server.js
      await api.put(`/admin/salary/${id}`, form);
      alert("‚úÖ C·∫≠p nh·∫≠t l∆∞∆°ng th√†nh c√¥ng");
      setEditId(null);
      setForm({ dailyRate: "", overtimeRate: "" });
      load();
    } catch (err) {
      console.error("‚ùå Update Error:", err.response?.data || err.message);
      alert("‚ùå L·ªói c·∫≠p nh·∫≠t l∆∞∆°ng");
    }
  };

  const cancelEdit = () => {
    setEditId(null);
    setForm({ dailyRate: "", overtimeRate: "" });
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="admin" /></div>
        <div className="col-9">
          <h3>üí∞ Qu·∫£n l√Ω l∆∞∆°ng (Admin)</h3>
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Nh√¢n vi√™n</th>
                <th>Ng√†y c√¥ng</th>
                <th>Ph·∫°t (VND)</th>
                <th>TƒÉng ca (gi·ªù)</th>
                <th>Ti·ªÅn ng√†y (VND)</th>
                <th>Ti·ªÅn tƒÉng ca (VND/gi·ªù)</th>
                <th>T·ªïng l∆∞∆°ng (VND)</th>
                <th>H√†nh ƒë·ªông</th>
              </tr>
            </thead>
            <tbody>
              {salaries.map((s) => (
                <tr key={s._id}>
                  <td>{s.name} ({s.userId?.username})</td>
                  <td>{s.totalDays ?? 0}</td>
                  <td>{(s.penalty ?? 0).toLocaleString("vi-VN")}</td>
                  <td>{s.overtimeHours ?? 0}</td>
                  <td>
                    {editId === s._id ? (
                      <input
                        className="form-control"
                        value={form.dailyRate}
                        onChange={(e) =>
                          setForm({ ...form, dailyRate: e.target.value })
                        }
                      />
                    ) : (
                      (s.dailyRate ?? 0).toLocaleString("vi-VN")
                    )}
                  </td>
                  <td>
                    {editId === s._id ? (
                      <input
                        className="form-control"
                        value={form.overtimeRate}
                        onChange={(e) =>
                          setForm({ ...form, overtimeRate: e.target.value })
                        }
                      />
                    ) : (
                      (s.overtimeRate ?? 0).toLocaleString("vi-VN")
                    )}
                  </td>
                  <td>{(s.amount ?? 0).toLocaleString("vi-VN")}</td>
                  <td>
                    {editId === s._id ? (
                      <>
                        <button
                          className="btn btn-success btn-sm me-2"
                          onClick={() => saveEdit(s._id)}
                        >
                          L∆∞u
                        </button>
                        <button
                          className="btn btn-secondary btn-sm"
                          onClick={cancelEdit}
                        >
                          H·ªßy
                        </button>
                      </>
                    ) : (
                      <button
                        className="btn btn-warning btn-sm"
                        onClick={() => startEdit(s)}
                      >
                        S·ª≠a
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default SalaryManagementAdmin;
