import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

function WorkScheduleAdmin() {
  const [list, setList] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [form, setForm] = useState({
    task: "",
    department: "",
    assignedTo: "",
    startDate: "",
    endDate: "",
  });

  // Load l·ªãch l√†m vi·ªác + danh s√°ch nh√¢n vi√™n
  const load = async () => {
    try {
      const res = await api.get("/admin/work-schedule");
      setList(res.data);

      const empRes = await api.get("/employees"); // ‚úÖ S·ª≠a l·∫°i kh√¥ng b·ªã th·ª´a /api
      setEmployees(empRes.data);
    } catch (err) {
      console.error("‚ùå API Error:", err.response?.data || err.message);
      alert("‚ùå Kh√¥ng t·∫£i ƒë∆∞·ª£c d·ªØ li·ªáu");
    }
  };

  useEffect(() => {
    load();
  }, []);

  // Khi ch·ªçn nh√¢n vi√™n ‚Üí t·ª± ƒë·ªông l·∫•y department
  const handleEmployeeChange = (e) => {
    const userId = e.target.value;
    const emp = employees.find((emp) => emp.userId?._id === userId);
    setForm({
      ...form,
      assignedTo: userId,
      department: emp?.department || "",
    });
  };

  // Submit form th√™m l·ªãch
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post("/admin/work-schedule", form);
      alert("‚úÖ Th√™m l·ªãch l√†m vi·ªác th√†nh c√¥ng");
      setForm({
        task: "",
        department: "",
        assignedTo: "",
        startDate: "",
        endDate: "",
      });
      load();
    } catch (err) {
      console.error("‚ùå L·ªói th√™m l·ªãch:", err.response?.data || err.message);
      alert("‚ùå Kh√¥ng th√™m ƒë∆∞·ª£c l·ªãch");
    }
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3">
          <SidebarMenu role="admin" />
        </div>
        <div className="col-9">
          <h3>üìÖ Qu·∫£n l√Ω l·ªãch l√†m vi·ªác</h3>

          {/* Form th√™m l·ªãch */}
          <form className="mb-3" onSubmit={handleSubmit}>
            <div className="mb-2">
              <label className="form-label">Nhi·ªám v·ª•</label>
              <input
                type="text"
                className="form-control"
                value={form.task}
                onChange={(e) => setForm({ ...form, task: e.target.value })}
                required
              />
            </div>

            <div className="mb-2">
              <label className="form-label">Nh√¢n vi√™n</label>
              <select
                className="form-control"
                value={form.assignedTo}
                onChange={handleEmployeeChange}
                required
              >
                <option value="">-- Ch·ªçn nh√¢n vi√™n --</option>
                {employees.map((emp) => (
                  <option key={emp._id} value={emp.userId?._id}>
                    {emp.name} ({emp.userId?.username})
                  </option>
                ))}
              </select>
            </div>

            <div className="mb-2">
              <label className="form-label">Ph√≤ng ban</label>
              <input
                type="text"
                className="form-control"
                value={form.department}
                readOnly
              />
            </div>

            <div className="row">
              <div className="col">
                <label className="form-label">Ng√†y b·∫Øt ƒë·∫ßu</label>
                <input
                  type="date"
                  className="form-control"
                  value={form.startDate}
                  onChange={(e) =>
                    setForm({ ...form, startDate: e.target.value })
                  }
                  required
                />
              </div>
              <div className="col">
                <label className="form-label">Ng√†y k·∫øt th√∫c</label>
                <input
                  type="date"
                  className="form-control"
                  value={form.endDate}
                  onChange={(e) =>
                    setForm({ ...form, endDate: e.target.value })
                  }
                  required
                />
              </div>
            </div>

            <button className="btn btn-primary mt-3">‚ûï Th√™m l·ªãch</button>
          </form>

          {/* Danh s√°ch l·ªãch l√†m vi·ªác */}
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Nhi·ªám v·ª•</th>
                <th>Ph√≤ng ban</th>
                <th>Nh√¢n vi√™n</th>
                <th>B·∫Øt ƒë·∫ßu</th>
                <th>K·∫øt th√∫c</th>
              </tr>
            </thead>
            <tbody>
              {list.map((w) => (
                <tr key={w._id}>
                  <td>{w.task}</td>
                  <td>{w.department}</td>
                  <td>{w.assignedTo?.username}</td>
                  <td>{new Date(w.startDate).toLocaleDateString("vi-VN")}</td>
                  <td>{new Date(w.endDate).toLocaleDateString("vi-VN")}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default WorkScheduleAdmin;
