import React, { useEffect, useState } from "react";
import UserLayout from "../../layouts/UserLayout";
import api from "../../api";

export default function ManagerAttendance() {
  const [employees, setEmployees] = useState([]);
  const [selected, setSelected] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadEmployees();
  }, []);

  const loadEmployees = async () => {
    try {
      const res = await api.get("/manager/group");
      setEmployees(res.data || []);
    } catch (err) {
      alert("Kh√¥ng t·∫£i ƒë∆∞·ª£c danh s√°ch nh√¢n vi√™n b·∫°n qu·∫£n l√Ω");
    } finally {
      setLoading(false);
    }
  };

  const toggleSelect = (id) => {
    setSelected((prev) =>
      prev.includes(id) ? prev.filter((x) => x !== id) : [...prev, id]
    );
  };

  const checkin = async () => {
    if (selected.length === 0) return alert("Ch∆∞a ch·ªçn nh√¢n vi√™n");

    try {
      await api.post("/attendance/bulk-checkin", { userIds: selected });
      alert("‚úî ƒê√£ ch·∫•m c√¥ng th√†nh c√¥ng");
      setSelected([]);
    } catch (err) {
      alert(err?.response?.data?.error || "L·ªói khi ch·∫•m c√¥ng");
    }

    loadEmployees();
  };

  return (
    <UserLayout>
      <h2 className="mb-3">üïí Ch·∫•m c√¥ng nh√¢n vi√™n d∆∞·ªõi quy·ªÅn</h2>

      <div className="card p-3">
        {loading ? (
          <p>ƒêang t·∫£i danh s√°ch...</p>
        ) : employees.length === 0 ? (
          <p>Ch∆∞a c√≥ nh√¢n vi√™n n√†o thu·ªôc nh√≥m b·∫°n qu·∫£n l√Ω.</p>
        ) : (
          <table className="table table-bordered table-striped">
            <thead>
              <tr>
                <th style={{ width: "50px" }}></th>
                <th>Nh√¢n vi√™n</th>
                <th>T√†i kho·∫£n</th>
                <th>Ph√≤ng ban</th>
              </tr>
            </thead>
            <tbody>
              {employees.map((emp) => (
                <tr key={emp._id}>
                  <td className="text-center">
                    <input
                      type="checkbox"
                      checked={selected.includes(emp.userId?._id)}
                      onChange={() => toggleSelect(emp.userId?._id)}
                    />
                  </td>
                  <td>{emp.name || "Kh√¥ng r√µ"}</td>
                  <td>{emp.userId?.username}</td>
                  <td>{emp.department || "‚Äî"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}

        {/* N√∫t ch·∫•m c√¥ng */}
        {selected.length > 0 && (
          <button
            className="btn btn-primary mt-3"
            onClick={checkin}
          >
            ‚úî Ch·∫•m c√¥ng {selected.length} nh√¢n vi√™n ƒë√£ ch·ªçn
          </button>
        )}
      </div>

      {/* Style nh·ªè khi hover b·∫£ng */}
      <style>
        {`
          table tbody tr:hover {
            background-color: #f8f9fa;
          }
        `}
      </style>
    </UserLayout>
  );
}
