// frontend/src/pages/user/PerformanceReviewUser.js
import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

export default function PerformanceReviewUser() {
  const [list, setList] = useState([]);
  const [loading, setLoading] = useState(false);

  // 🔹 Tải danh sách đánh giá hiệu suất của chính mình
  const load = async () => {
    setLoading(true);
    try {
      const res = await api.get("/employees/performance"); // ✅ Sửa đúng endpoint
      setList(res.data || []);
    } catch (err) {
      console.error("❌ Lỗi tải dữ liệu hiệu suất:", err);
      alert(err.response?.data?.error || "Không thể tải dữ liệu hiệu suất!");
      setList([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    load();
  }, []);

  return (
    <div className="container mt-3">
      <div className="row">
        {/* Sidebar */}
        <div className="col-3">
          <SidebarMenu role="user" />
        </div>

        {/* Nội dung chính */}
        <div className="col-9">
          <h3 className="mb-3">📊 Đánh giá hiệu suất của tôi</h3>

          {loading ? (
            <div className="text-center text-muted p-3">
              Đang tải dữ liệu...
            </div>
          ) : (
            <table className="table table-bordered table-striped">
              <thead className="table-light">
                <tr>
                  <th style={{ width: "10%" }}>Điểm</th>
                  <th style={{ width: "50%" }}>Nhận xét</th>
                  <th style={{ width: "20%" }}>Người đánh giá</th>
                  <th style={{ width: "20%" }}>Ngày đánh giá</th>
                </tr>
              </thead>
              <tbody>
                {list.length > 0 ? (
                  list.map((r) => (
                    <tr key={r._id}>
                      <td className="text-center fw-bold">
                        {r.score ?? r.totalScore ?? "—"}
                      </td>
                      <td>{r.feedback || "Không có nhận xét"}</td>
                      <td>{r.reviewer || "Quản trị viên"}</td>
                      <td>
                        {new Date(r.createdAt).toLocaleString("vi-VN", {
                          hour12: false,
                        })}
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan={4} className="text-center text-muted">
                      Chưa có đánh giá nào
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          )}
        </div>
      </div>
    </div>
  );
}
