import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

// ================= Helpers =================
const fmtMoney = (v) =>
  typeof v === "number"
    ? v.toLocaleString("vi-VN")
    : (Number(v || 0)).toLocaleString("vi-VN");

const ymNow = () => {
  const d = new Date();
  return `${d.getFullYear()}-${String(d.getMonth() + 1).padStart(2, "0")}`;
};

// ================= Component =================
export default function SalaryManagement() {
  const [month, setMonth] = useState(ymNow());
  const [salary, setSalary] = useState(null);
  const [loading, setLoading] = useState(false);

  const load = async () => {
    try {
      setLoading(true);
      const res = await api.get("/salary/me");
      const data = res.data?.[0];
      setSalary(data || null);
    } catch (err) {
      console.error("‚ùå Salary load error:", err.response?.data || err.message);
      alert("‚ùå Kh√¥ng t·∫£i ƒë∆∞·ª£c d·ªØ li·ªáu l∆∞∆°ng");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    load();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [month]);

  if (loading) {
    return (
      <div className="container mt-5 text-center">
        <div className="spinner-border text-primary" role="status"></div>
        <div className="mt-2">ƒêang t·∫£i d·ªØ li·ªáu...</div>
      </div>
    );
  }

  if (!salary) {
    return (
      <div className="container mt-3">
        <div className="row">
          <div className="col-3">
            <SidebarMenu role="employee" />
          </div>
          <div className="col-9">
            <h3>üí∞ L∆∞∆°ng c·ªßa t√¥i</h3>
            <div className="alert alert-info">Kh√¥ng c√≥ d·ªØ li·ªáu l∆∞∆°ng trong th√°ng n√†y.</div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-3">
      <div className="row">
        {/* Sidebar */}
        <div className="col-3">
          <SidebarMenu role="employee" />
        </div>

        {/* N·ªôi dung ch√≠nh */}
        <div className="col-9">
          {/* Header */}
          <div className="d-flex align-items-center justify-content-between mb-3">
            <h3 className="mb-0">üí∞ L∆∞∆°ng c·ªßa t√¥i</h3>
            <div className="d-flex gap-2 align-items-center">
              <input
                type="month"
                className="form-control"
                style={{ width: 180 }}
                value={month}
                onChange={(e) => setMonth(e.target.value)}
              />
              <button
                className="btn btn-outline-secondary"
                onClick={load}
                disabled={loading}
              >
                ‚ü≥ {loading ? "ƒêang t·∫£i..." : "L√†m m·ªõi"}
              </button>
            </div>
          </div>

          {/* Th√¥ng tin t·ªïng quan */}
          <div className="row g-2 mb-3">
            <div className="col">
              <div className="card p-2 text-center">
                <div className="small text-muted">Th√°ng</div>
                <div className="fw-bold">{salary.month}</div>
              </div>
            </div>

            <div className="col">
              <div className="card p-2 text-center">
                <div className="small text-muted">Ng√†y c√¥ng</div>
                <div className="fw-bold">{salary.totalDays}</div>
              </div>
            </div>

