import React from "react";
import { Link } from "react-router-dom";

export default function Sidebar() {
  return (
    <div className="bg-light vh-100 p-3">
      <ul className="nav flex-column">
        <li className="nav-item"><Link to="/dashboard" className="nav-link">Dashboard</Link></li>
        <li className="nav-item"><Link to="/employees" className="nav-link">Employees</Link></li>
        <li className="nav-item"><Link to="/attendance" className="nav-link">Attendance</Link></li>
        <li className="nav-item"><Link to="/leave-requests" className="nav-link">Leave Requests</Link></li>
        <li className="nav-item"><Link to="/messages" className="nav-link">Messages</Link></li>
      </ul>
    </div>
  );
}
