import React from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import { AuthProvider } from "./context/AuthContext";
import Protected from "./components/Protected";

// Public
import Login from "./pages/Login";
import Register from "./pages/Register";

// Admin
import AdminDashboard from "./pages/admin/AdminDashboard";
import HRManagement from "./pages/admin/HRManagement";
import LeaveApproval from "./pages/admin/LeaveApproval";
import AttendanceManagement from "./pages/admin/AttendanceManagement";

// User
import UserDashboard from "./pages/user/UserDashboard";
import ProfileUpdate from "./pages/user/ProfileUpdate";
import LeaveRequest from "./pages/user/LeaveRequest";
import LeaveHistory from "./pages/user/LeaveHistory";
import Attendance from "./pages/user/Attendance";
import Chat from "./pages/user/Chat";
import Report from "./pages/user/Report";

// Shared
import Notifications from "./pages/shared/Notifications";
import SalaryManagement from "./pages/shared/SalaryManagement";
import PerformanceReview from "./pages/shared/PerformanceReview";
import DocumentManagement from "./pages/shared/DocumentManagement";
import WorkSchedule from "./pages/shared/WorkSchedule";

function App() {
  return (
    <BrowserRouter>
      <AuthProvider>
        <Routes>
          {/* Public */}
          <Route path="/" element={<Login />} />
          <Route path="/register" element={<Register />} />

          {/* ================= ADMIN ================= */}
          <Route
            path="/admin/dashboard"
            element={<Protected roles={["admin"]}><AdminDashboard /></Protected>}
          />
          <Route
            path="/admin/hr-management"
            element={<Protected roles={["admin"]}><HRManagement /></Protected>}
          />
          <Route
            path="/admin/leave-approval"
            element={<Protected roles={["admin"]}><LeaveApproval /></Protected>}
          />
          <Route
            path="/admin/attendance-management"
            element={<Protected roles={["admin"]}><AttendanceManagement /></Protected>}
          />
          <Route
            path="/admin/notifications"
            element={<Protected roles={["admin"]}><Notifications role="admin" /></Protected>}
          />
          <Route
            path="/admin/salary-management"
            element={<Protected roles={["admin"]}><SalaryManagement role="admin" /></Protected>}
          />
          <Route
            path="/admin/performance-review"
            element={<Protected roles={["admin"]}><PerformanceReview role="admin" /></Protected>}
          />
          <Route
            path="/admin/document-management"
            element={<Protected roles={["admin"]}><DocumentManagement role="admin" /></Protected>}
          />
          <Route
            path="/admin/work-schedule"
            element={<Protected roles={["admin"]}><WorkSchedule role="admin" /></Protected>}
          />

          {/* ================= USER ================= */}
          <Route
            path="/user/dashboard"
            element={<Protected roles={["employee","manager"]}><UserDashboard /></Protected>}
          />
          <Route
            path="/user/profile-update"
            element={<Protected roles={["employee","manager"]}><ProfileUpdate /></Protected>}
          />
          <Route
            path="/user/leave-request"
            element={<Protected roles={["employee","manager"]}><LeaveRequest /></Protected>}
          />
          <Route
            path="/user/leave-history"
            element={<Protected roles={["employee","manager"]}><LeaveHistory /></Protected>}
          />
          <Route
            path="/user/attendance"
            element={<Protected roles={["employee","manager"]}><Attendance /></Protected>}
          />
          <Route
            path="/user/chat"
            element={<Protected roles={["employee","manager"]}><Chat /></Protected>}
          />
          <Route
            path="/user/report"
            element={<Protected roles={["employee","manager"]}><Report /></Protected>}
          />
          <Route
            path="/user/notifications"
            element={<Protected roles={["employee","manager"]}><Notifications role="user" /></Protected>}
          />
          <Route
            path="/user/salary-management"
            element={<Protected roles={["employee","manager"]}><SalaryManagement role="user" /></Protected>}
          />
          <Route
            path="/user/performance-review"
            element={<Protected roles={["employee","manager"]}><PerformanceReview role="user" /></Protected>}
          />
          <Route
            path="/user/document-management"
            element={<Protected roles={["employee","manager"]}><DocumentManagement role="user" /></Protected>}
          />
          <Route
            path="/user/work-schedule"
            element={<Protected roles={["employee","manager"]}><WorkSchedule role="user" /></Protected>}
          />
        </Routes>
      </AuthProvider>
    </BrowserRouter>
  );
}

export default App;
