// src/pages/Login.js
import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

import "../styles/auth.css";
import "../styles/login.css";

function Login() {
  const navigate = useNavigate();
  const { login } = useAuth();

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const res = await axios.post("http://localhost:5000/api/auth/login", {
        username,
        password,
      });

      // L∆ØU ƒê√öNG CHU·∫®N
      login({
        token: res.data.token,
        refreshToken: res.data.refreshToken,
        user: res.data.user,
      });

      const role = res.data.user.role;

      role === "admin"
        ? navigate("/admin/dashboard")
        : navigate("/user/dashboard");
    } catch (err) {
      alert(err.response?.data?.error || "Sai t√™n ƒëƒÉng nh·∫≠p ho·∫∑c m·∫≠t kh·∫©u!");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2 className="auth-title">üîê ƒêƒÉng nh·∫≠p h·ªá th·ªëng</h2>
        <p className="auth-sub">Qu·∫£n l√Ω nh√¢n s·ª± - HRM System</p>

        <form onSubmit={handleLogin} className="auth-form">
          <div className="mb-3">
            <label className="form-label">T√™n ƒëƒÉng nh·∫≠p</label>
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Nh·∫≠p t√™n ƒëƒÉng nh·∫≠p..."
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
              autoFocus
            />
          </div>

          <div className="mb-3">
            <label className="form-label">M·∫≠t kh·∫©u</label>
            <input
              type="password"
              className="form-control form-control-lg"
              placeholder="Nh·∫≠p m·∫≠t kh·∫©u..."
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          <button className="btn btn-primary w-100 auth-btn" disabled={loading}>
            {loading ? "‚è≥ ƒêang ƒëƒÉng nh·∫≠p..." : "ƒêƒÉng nh·∫≠p"}
          </button>
        </form>

        <p className="auth-switch">
          Ch∆∞a c√≥ t√†i kho·∫£n?{" "}
          <span onClick={() => navigate("/register")} className="auth-link">
            ƒêƒÉng k√Ω ngay
          </span>
        </p>
      </div>
    </div>
  );
}

export default Login;
