// frontend/src/pages/user/Attendance.js
import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

export default function Attendance() {
  const [list, setList] = useState([]);
  const [status, setStatus] = useState({ isCheckedIn: false, today: null });

  const load = async () => {
    const res = await api.get("/attendance/me"); // gợi ý: tạo route riêng cho user
    setList(res.data.history || []);
    setStatus(res.data.status || { isCheckedIn: false, today: null });
  };

  useEffect(()=>{ load(); },[]);

  const checkIn = async () => { await api.post("/attendance/checkin"); load(); };
  const checkOut = async () => { await api.post("/attendance/checkout"); load(); };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="user" /></div>
        <div className="col-9">
          <h3>⏰ Chấm công</h3>
          <div className="mb-3">
            {status.isCheckedIn
              ? <button className="btn btn-danger" onClick={checkOut}>Check-out</button>
              : <button className="btn btn-success" onClick={checkIn}>Check-in</button>
            }
          </div>
          <table className="table table-bordered">
            <thead><tr><th>Ngày</th><th>Vào</th><th>Ra</th><th>Giờ</th></tr></thead>
            <tbody>
              {list.map((r,i) => (
                <tr key={i}>
                  <td>{new Date(r.date).toLocaleDateString("vi-VN")}</td>
                  <td>{r.checkInTime ? new Date(r.checkInTime).toLocaleTimeString("vi-VN") : ""}</td>
                  <td>{r.checkOutTime ? new Date(r.checkOutTime).toLocaleTimeString("vi-VN") : ""}</td>
                  <td>{r.totalHours || 0}</td>
                </tr>
              ))}
              {list.length === 0 && (
                <tr><td colSpan={4} className="text-center text-muted">Chưa có lịch sử</td></tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
