// frontend/src/pages/admin/SalaryManagementAdmin.js
import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

function SalaryManagementAdmin() {
  const [salaries, setSalaries] = useState([]);
  const [editId, setEditId] = useState(null);
  const [form, setForm] = useState({ hourlyRate: "" });

  const load = async () => {
    try {
      const res = await api.get("/admin/salary");
      setSalaries(res.data);
    } catch (err) {
      console.error("‚ùå API Error:", err.response?.data || err.message);
      alert("‚ùå Kh√¥ng t·∫£i ƒë∆∞·ª£c d·ªØ li·ªáu l∆∞∆°ng");
    }
  };

  useEffect(() => { load(); }, []);

  const startEdit = (s) => {
    setEditId(s._id);
    setForm({ hourlyRate: s.hourlyRate });
  };

  const saveEdit = async (id) => {
    try {
      await api.put(`/admin/salary/${id}`, form);
      alert("‚úÖ C·∫≠p nh·∫≠t l∆∞∆°ng th√†nh c√¥ng");
      setEditId(null);
      setForm({ hourlyRate: "" });
      load();
    } catch (err) {
      console.error("‚ùå Update Error:", err.response?.data || err.message);
      alert("‚ùå L·ªói c·∫≠p nh·∫≠t l∆∞∆°ng");
    }
  };

  const cancelEdit = () => {
    setEditId(null);
    setForm({ hourlyRate: "" });
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="admin" /></div>
        <div className="col-9">
          <h3>üí∞ Qu·∫£n l√Ω l∆∞∆°ng (admin)</h3>
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Nh√¢n vi√™n</th>
                <th>T·ªïng gi·ªù l√†m</th>
                <th>Ti·ªÅn gi·ªù (VND)</th>
                <th>L∆∞∆°ng (VND)</th>
                <th>H√†nh ƒë·ªông</th>
              </tr>
            </thead>
            <tbody>
              {salaries.map((s) => (
                <tr key={s._id}>
                  <td>{s.name} ({s.userId?.username})</td>
                  <td>{s.totalHours}</td>
                  <td>
                    {editId === s._id ? (
                      <input
                        className="form-control"
                        value={form.hourlyRate}
                        onChange={(e) =>
                          setForm({ ...form, hourlyRate: e.target.value })
                        }
                      />
                    ) : (
                      s.hourlyRate.toLocaleString("vi-VN")
                    )}
                  </td>
                  <td>{s.amount.toLocaleString("vi-VN")}</td>
                  <td>
                    {editId === s._id ? (
                      <>
                        <button
                          className="btn btn-success btn-sm me-2"
                          onClick={() => saveEdit(s._id)}
                        >
                          L∆∞u
                        </button>
                        <button
                          className="btn btn-secondary btn-sm"
                          onClick={cancelEdit}
                        >
                          H·ªßy
                        </button>
                      </>
                    ) : (
                      <button
                        className="btn btn-warning btn-sm"
                        onClick={() => startEdit(s)}
                      >
                        S·ª≠a
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default SalaryManagementAdmin;
