import React, { useState, useEffect } from "react";
import Layout from "../components/Layout";
import { io } from "socket.io-client";

const socket = io("http://localhost:5000");

export default function Messages() {
  const [msg, setMsg] = useState("");
  const [list, setList] = useState([]);

  useEffect(() => {
    socket.on("receive_message", m => setList(prev => [...prev, m]));
    return () => socket.off("receive_message");
  }, []);

  const send = () => {
    socket.emit("send_message", { content: msg });
    setMsg("");
  };

  return (
    <Layout title="Chat nội bộ">
      <div style={{height:200, overflowY:"auto"}} className="border mb-3 p-2">
        {list.map((m,i)=><div key={i}>{m.content}</div>)}
      </div>
      <input className="form-control mb-2" value={msg} onChange={e=>setMsg(e.target.value)} />
      <button className="btn btn-success" onClick={send}>Gửi</button>
    </Layout>
  );
}
