import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

function DocumentManagementAdmin() {
  const [docs, setDocs] = useState([]);
  const [folders, setFolders] = useState([]); // danh s√°ch th∆∞ m·ª•c
  const [form, setForm] = useState({
    folder: "",
    department: "general",
    files: [],
  });
  const [newFolder, setNewFolder] = useState("");

  // T·∫£i danh s√°ch t√†i li·ªáu
  const load = async () => {
    try {
      const res = await api.get("/admin/documents");
      setDocs(res.data);

      // L·∫•y t·∫•t c·∫£ c√°c th∆∞ m·ª•c duy nh·∫•t
      const uniqueFolders = [...new Set(res.data.map((d) => d.folder || "Chung"))];
      setFolders(uniqueFolders);
    } catch (err) {
      alert("‚ùå L·ªói t·∫£i danh s√°ch t√†i li·ªáu");
    }
  };

  useEffect(() => {
    load();
  }, []);

  // ======= T·∫†O TH∆Ø M·ª§C =======
  const handleCreateFolder = async (e) => {
    e.preventDefault();
    if (!newFolder.trim()) return alert("‚ö†Ô∏è Vui l√≤ng nh·∫≠p t√™n th∆∞ m·ª•c");
    try {
      await api.post("/admin/documents/folder", { folder: newFolder.trim() });
      alert(`‚úÖ ƒê√£ t·∫°o th∆∞ m·ª•c '${newFolder}'`);
      setNewFolder("");
      setFolders([...folders, newFolder.trim()]);
    } catch {
      alert("‚ùå L·ªói t·∫°o th∆∞ m·ª•c");
    }
  };

  // ======= UPLOAD FILES =======
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.folder) return alert("‚ö†Ô∏è Vui l√≤ng ch·ªçn th∆∞ m·ª•c");
    if (!form.files || form.files.length === 0) return alert("‚ö†Ô∏è Ch∆∞a ch·ªçn file");

    try {
      const fd = new FormData();
      fd.append("department", form.department);
      fd.append("folder", form.folder);
      for (let file of form.files) fd.append("files", file);

      await api.post("/admin/documents", fd, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("‚úÖ Upload th√†nh c√¥ng");
      setForm({ folder: "", department: "general", files: [] });
      load();
    } catch {
      alert("‚ùå L·ªói upload file");
    }
  };

  // ======= X√ìA FILE =======
  const handleDelete = async (id) => {
    if (!window.confirm("B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a t√†i li·ªáu n√†y?")) return;
    try {
      await api.delete(`/admin/documents/${id}`);
      load();
    } catch {
      alert("‚ùå L·ªói khi x√≥a file");
    }
  };

  // ======= T·∫¢I FILE =======
  const handleDownload = (id) => {
    window.open(`http://localhost:5000/api/admin/documents/download/${id}`, "_blank");
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3">
          <SidebarMenu role="admin" />
        </div>

        <div className="col-9">
          <h3>üìÇ Qu·∫£n l√Ω t√†i li·ªáu</h3>

          {/* ====== T·∫†O TH∆Ø M·ª§C ====== */}
          <form onSubmit={handleCreateFolder} className="card p-3 mb-3">
            <h5>üìÅ T·∫°o th∆∞ m·ª•c m·ªõi</h5>
            <div className="d-flex">
              <input
                type="text"
                className="form-control me-2"
                placeholder="T√™n th∆∞ m·ª•c..."
                value={newFolder}
                onChange={(e) => setNewFolder(e.target.value)}
              />
              <button className="btn btn-success">‚ûï T·∫°o</button>
            </div>
          </form>

          {/* ====== UPLOAD FILE ====== */}
          <form onSubmit={handleSubmit} className="card p-3 mb-3">
            <h5>üì§ Upload t√†i li·ªáu</h5>

            <div className="row">
              <div className="col-md-4 mb-2">
                <label>Ph√≤ng ban</label>
                <select
                  className="form-control"
                  value={form.department}
                  onChange={(e) => setForm({ ...form, department: e.target.value })}
                >
                  <option value="general">Chung</option>
                  <option value="IT">IT</option>
                  <option value="HR">HR</option>
                  <option value="Finance">Finance</option>
                </select>
              </div>

              <div className="col-md-4 mb-2">
                <label>Th∆∞ m·ª•c</label>
                <select
                  className="form-control"
                  value={form.folder}
                  onChange={(e) => setForm({ ...form, folder: e.target.value })}
                >
                  <option value="">-- Ch·ªçn th∆∞ m·ª•c --</option>
                  {folders.map((f, idx) => (
                    <option key={idx} value={f}>
                      {f}
                    </option>
                  ))}
                </select>
              </div>

              <div className="col-md-4 mb-2">
                <label>Ch·ªçn file</label>
                <input
                  type="file"
                  className="form-control"
                  multiple
                  onChange={(e) => setForm({ ...form, files: e.target.files })}
                />
              </div>
            </div>

            <button className="btn btn-primary mt-2">üì§ Upload</button>
          </form>

          {/* ====== DANH S√ÅCH T√ÄI LI·ªÜU ====== */}
          <div className="card p-3">
            <h5>üìë Danh s√°ch t√†i li·ªáu</h5>
            {docs.length === 0 ? (
              <p>Ch∆∞a c√≥ t√†i li·ªáu n√†o.</p>
            ) : (
              <table className="table table-bordered mt-2">
                <thead>
                  <tr>
                    <th>Th∆∞ m·ª•c</th>
                    <th>Ti√™u ƒë·ªÅ</th>
                    <th>Ph√≤ng ban</th>
                    <th>Ng∆∞·ªùi t·∫£i l√™n</th>
                    <th>H√†nh ƒë·ªông</th>
                  </tr>
                </thead>
                <tbody>
                  {docs.map((d) => (
                    <tr key={d._id}>
                      <td>{d.folder || "Chung"}</td>
                      <td>{d.title}</td>
                      <td>{d.department}</td>
                      <td>{d.uploadedBy?.username}</td>
                      <td>
                        <button
                          className="btn btn-sm btn-success me-2"
                          onClick={() => handleDownload(d._id)}
                        >
                          ‚¨áÔ∏è T·∫£i
                        </button>
                        <button
                          className="btn btn-sm btn-danger"
                          onClick={() => handleDelete(d._id)}
                        >
                          üóë X√≥a
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default DocumentManagementAdmin;
