import React, { useEffect, useMemo, useRef, useState } from "react";
import io from "socket.io-client";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";
import { useAuth } from "../../context/AuthContext";

const socket = io(
  process.env.REACT_APP_API_BASE?.replace("/api", "") || "http://localhost:5000",
  { transports: ["websocket"] }
);

export default function Chat() {
  const { user } = useAuth(); // { user: {_id, username, role, department? ...} }
  const me = user?.user;

  const [deptRoom, setDeptRoom] = useState(null);
  const [privateRooms, setPrivateRooms] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [chatOutsideDept, setChatOutsideDept] = useState(false);
  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");

  const bottomRef = useRef();
  const myDepartment = useMemo(() => me?.department || null, [me]);

  // üß© Join socket rooms khi c√≥ me
  useEffect(() => {
    if (me?._id) {
      socket.emit("join", { userId: me._id, department: me.department });
    }
  }, [me]);

  // üóÇ L·∫•y danh s√°ch rooms (dept + private)
  const loadRooms = async () => {
    try {
      const res = await api.get("/messages/rooms");
      setPrivateRooms(res.data?.privateRooms || []);
      setDeptRoom(res.data?.deptRoom || null);
    } catch (err) {
      console.error("‚ùå L·ªói load rooms:", err);
    }
  };

  // üë• L·∫•y danh s√°ch nh√¢n vi√™n (trong ph√≤ng ho·∫∑c to√†n c√¥ng ty)
  const loadEmployees = async () => {
    try {
      const scope = chatOutsideDept ? "all" : "dept";
      const res = await api.get(`/employees/peers?scope=${scope}`);
      setEmployees(Array.isArray(res.data) ? res.data : []);
    } catch (err) {
      console.error("‚ùå L·ªói load danh s√°ch nh√¢n vi√™n:", err);
      setEmployees([]);
    }
  };

  // üí¨ L·∫•y tin nh·∫Øn c·ªßa ph√≤ng
  const loadMessages = async (room) => {
    try {
      const res = await api.get(`/messages/${room._id}`);
      setMessages(res.data || []);
      setTimeout(() => bottomRef.current?.scrollIntoView({ behavior: "smooth" }), 50);
    } catch (err) {
      console.error("‚ùå L·ªói load tin nh·∫Øn:", err);
      setMessages([]);
    }
  };

  // ‚è± Load rooms + employees khi m·ªü trang (v√† ƒë√£ c√≥ me)
  useEffect(() => {
    if (!me?._id) return;
    loadRooms();
    loadEmployees(); // ‚úÖ quan tr·ªçng: load danh s√°ch nh√¢n vi√™n m·∫∑c ƒë·ªãnh (trong ph√≤ng ban)
  }, [me]);

  // üîÑ Reload employees khi b·∫≠t/t·∫Øt "chat ngo√†i ph√≤ng ban"
  useEffect(() => {
    if (!me?._id) return;
    loadEmployees();
  }, [chatOutsideDept, me]);

  // üîî Socket realtime: nh·∫≠n tin nh·∫Øn
  useEffect(() => {
    const handler = (payload) => {
      if (!currentRoom) return;
      if (payload.roomId && payload.roomId !== currentRoom._id) return;

      setMessages((prev) => [
        ...prev,
        {
          _id: Math.random().toString(36).slice(2),
          sender: { username: payload.fromUsername || "N/A" },
          content: payload.message,
          createdAt: new Date().toISOString(),
        },
      ]);
      bottomRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    socket.on("receive_message", handler);
    return () => socket.off("receive_message", handler);
  }, [currentRoom]);

  const openRoom = async (room) => {
    setCurrentRoom(room);
    await loadMessages(room);
  };

  const startPrivateWith = async (otherUserId) => {
    try {
      const res = await api.post("/messages/rooms/private", { otherUserId });
      await loadRooms();
      setCurrentRoom(res.data);
      await loadMessages(res.data);
    } catch (err) {
      console.error("‚ùå L·ªói t·∫°o private room:", err);
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!text.trim() || !currentRoom) return;

    try {
      // L∆∞u DB
      const saved = await api.post("/messages", {
        roomId: currentRoom._id,
        content: text.trim(),
      });

      // Realtime
      if (currentRoom.type === "group") {
        socket.emit("send_message", {
          type: "group",
          department: currentRoom.department,
          roomId: currentRoom._id,
          fromUserId: me?._id,
          fromUsername: me?.username,
          message: saved.data.content,
        });
      } else {
        const others = (currentRoom.participants || [])
          .map((p) => p._id)
          .filter((id) => id !== me?._id);
        const toUserId = others?.[0];

        socket.emit("send_message", {
          type: "private",
          toUserId,
          roomId: currentRoom._id,
          fromUserId: me?._id,
          fromUsername: me?.username,
          message: saved.data.content,
        });
      }

      setMessages((prev) => [...prev, saved.data]);
      setText("");
      bottomRef.current?.scrollIntoView({ behavior: "smooth" });
    } catch (err) {
      console.error("‚ùå L·ªói g·ª≠i tin nh·∫Øn:", err);
    }
  };

  return (
    <div className="container-fluid mt-3">
      <div className="row">
        {/* Sidebar */}
        <div className="col-3">
          <SidebarMenu role="user" />
          <div className="card mt-3">
            <div className="card-header">
              <strong>Chat</strong>
            </div>
            <div className="card-body p-2">
              <div className="form-check form-switch mb-2">
                <input
                  className="form-check-input"
                  type="checkbox"
                  checked={chatOutsideDept}
                  onChange={() => setChatOutsideDept((v) => !v)}
                  id="switchOutside"
                />
                <label htmlFor="switchOutside">Chat ngo√†i ph√≤ng ban</label>
              </div>

              {/* Ph√≤ng ban */}
              {deptRoom && (
                <div className="mb-3">
                  <div className="d-flex justify-content-between align-items-center">
                    <strong>üë• {deptRoom.name}</strong>
                    <button
                      className="btn btn-sm btn-outline-primary"
                      onClick={() => openRoom(deptRoom)}
                    >
                      M·ªü
                    </button>
                  </div>
                </div>
              )}

              {/* Danh s√°ch nh√¢n vi√™n */}
              <hr />
              <small className="text-muted">
                Nh√¢n vi√™n {chatOutsideDept ? "to√†n c√¥ng ty" : "trong ph√≤ng ban"}
              </small>
              <div className="list-group mt-1" style={{ maxHeight: 320, overflowY: "auto" }}>
                {employees.length === 0 ? (
                  <div className="text-muted small px-2">Kh√¥ng c√≥ nh√¢n vi√™n n√†o</div>
                ) : (
                  employees
                    // chu·∫©n ho√° d·ªØ li·ªáu t·ª´ API peers (c√≥ th·ªÉ l√† {userId,username,name,department})
                    .map((e) => ({
                      _id: e._id || e.userId, // id c·ªßa employee record ho·∫∑c userId
                      userId: e.userId,
                      username: e.username,
                      name: e.name || "Ch∆∞a ƒë·∫∑t t√™n",
                      department: e.department,
                    }))
                    // ·∫©n ch√≠nh m√¨nh
                    .filter((e) => e.userId !== me?._id)
                    // n·∫øu kh√¥ng b·∫≠t outsideDept ‚Üí ph·∫£i c√πng ph√≤ng
                    .filter((e) => (chatOutsideDept ? true : e.department === myDepartment))
                    .map((e) => (
                      <button
                        key={`${e._id}-${e.userId}`}
                        className="list-group-item list-group-item-action"
                        onClick={() => startPrivateWith(e.userId)}
                      >
                        {e.name} <span className="text-muted">({e.username})</span>
                      </button>
                    ))
                )}
              </div>

              {/* Private rooms */}
              <hr className="my-2" />
              <small className="text-muted">ƒêo·∫°n chat g·∫ßn ƒë√¢y</small>
              <div className="list-group mt-1" style={{ maxHeight: 200, overflowY: "auto" }}>
                {privateRooms.map((r) => (
                  <button
                    key={r._id}
                    className="list-group-item list-group-item-action"
                    onClick={() => openRoom(r)}
                  >
                    {r.participants?.map((p) => p.username).join(" , ")}
                  </button>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* Khung chat */}
        <div className="col-9">
          <div className="card">
            <div className="card-header">
              {currentRoom
                ? currentRoom.type === "group"
                  ? `üë• ${currentRoom.name}`
                  : `üí¨ ${(currentRoom.participants || []).map((p) => p.username).join(" , ")}`
                : "Ch·ªçn 1 ƒëo·∫°n h·ªôi tho·∫°i"}
            </div>
            <div className="card-body" style={{ height: 520, overflowY: "auto" }}>
              {messages.map((m) => (
                <div
                  key={m._id}
                  className={`mb-2 ${m.sender?._id === me?._id ? "text-end" : ""}`}
                >
                  <div>
                    <small className="text-muted">
                      {m.sender?.username} ¬∑ {new Date(m.createdAt).toLocaleString()}
                    </small>
                  </div>
                  <div
                    className={`d-inline-block p-2 rounded ${
                      m.sender?._id === me?._id ? "bg-primary text-white" : "bg-light"
                    }`}
                  >
                    {m.content}
                  </div>
                </div>
              ))}
              <div ref={bottomRef} />
            </div>
            <div className="card-footer">
              <form onSubmit={sendMessage} className="d-flex gap-2">
                <input
                  className="form-control"
                  placeholder="Nh·∫≠p tin nh·∫Øn..."
                  value={text}
                  onChange={(e) => setText(e.target.value)}
                />
                <button className="btn btn-primary" disabled={!currentRoom || !text.trim()}>
                  G·ª≠i
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
