import React, { useEffect, useMemo, useState } from "react";
import api from "../../api";
import AdminLayout from "../../layouts/AdminLayout";

// ================= Helper =================
const fmtMoney = (v) =>
  typeof v === "number"
    ? v.toLocaleString("vi-VN")
    : (Number(v || 0)).toLocaleString("vi-VN");

const ymNow = () => {
  const d = new Date();
  return `${d.getFullYear()}-${String(d.getMonth() + 1).padStart(2, "0")}`;
};

// ================= Component =================
export default function SalaryManagementAdmin() {
  const [month, setMonth] = useState(ymNow());
  const [salaries, setSalaries] = useState([]);
  const [loading, setLoading] = useState(false);

  const [editId, setEditId] = useState(null);
  const [form, setForm] = useState({
    dailyRate: "",
    overtimeRate: "",
    penalty: "",
    totalDays: "",
  });

  // ================= T·ªïng h·ª£p th·ªëng k√™ =================
  const totalSummary = useMemo(() => {
    const sum = (k) => salaries.reduce((acc, s) => acc + Number(s[k] || 0), 0);
    return {
      totalDays: sum("totalDays"),
      overtimeHours: sum("overtimeHours"),
      penalty: sum("penalty"),
      overtimePay: sum("overtimePay"),
      amount: sum("amount"),
    };
  }, [salaries]);

  // ================= Load d·ªØ li·ªáu =================
  const load = async () => {
    try {
      setLoading(true);
      const res = await api.get(`/salary?month=${encodeURIComponent(month)}`);

      const data = (res.data || []).map((s) => ({
        ...s,
        displayName: s.name || s.userId?.username || s.username || "Ch∆∞a c√≥ t√™n",
        month: s.month || month,
        dailyRate: s.dailyRate ?? 0,
        overtimeRate: s.overtimeRate ?? 0,
        overtimePay:
          s.overtimePay ??
          Number(s.overtimeHours || 0) * Number(s.overtimeRate || 0),
