import React, { useEffect, useState } from "react";
import UserLayout from "../../layouts/UserLayout";
import api from "../../api";

export default function ManageGroup() {
  const [departmentEmployees, setDepartmentEmployees] = useState([]);
  const [group, setGroup] = useState([]);
  const [selected, setSelected] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);

      // L·∫•y nh√¢n vi√™n c√πng ph√≤ng ban
      const deptRes = await api.get("/manager/department-employees");

      // L·∫•y nh√¢n vi√™n thu·ªôc nh√≥m manager
      const groupRes = await api.get("/manager/group");

      setDepartmentEmployees(deptRes.data || []);
      setGroup(groupRes.data || []);
    } catch (err) {
      alert("Kh√¥ng th·ªÉ t·∫£i danh s√°ch nh√¢n vi√™n!");
    } finally {
      setLoading(false);
    }
  };

  const toggleSelect = (id) => {
    setSelected((prev) =>
      prev.includes(id) ? prev.filter((x) => x !== id) : [...prev, id]
    );
  };

  const addToGroup = async () => {
    if (selected.length === 0) return alert("Ch∆∞a ch·ªçn nh√¢n vi√™n n√†o");

    try {
      await api.post("/manager/group/add", {
        employeeIds: selected
      });

      alert("‚úî ƒê√£ th√™m nh√¢n vi√™n v√†o nh√≥m");

      setSelected([]);
      loadData(); // Reload l·∫°i danh s√°ch
    } catch (err) {
      alert(err?.response?.data?.error || "L·ªói khi th√™m nh√¢n vi√™n v√†o nh√≥m");
    }
  };

  return (
    <UserLayout>
      <h2 className="mb-3">üë• Qu·∫£n l√Ω nh√≥m nh√¢n vi√™n</h2>

      {/* =================== DANH S√ÅCH NH√ÇN VI√äN C√ôNG PH√íNG BAN =================== */}
      <div className="card p-3 mb-4">
        <h5>Nh√¢n vi√™n c√πng ph√≤ng ban</h5>
        <p className="text-muted mb-2">Ch·ªçn nh√¢n vi√™n ƒë·ªÉ th√™m v√†o nh√≥m b·∫°n qu·∫£n l√Ω.</p>

        {loading ? (
          <p>ƒêang t·∫£i...</p>
        ) : departmentEmployees.length === 0 ? (
          <p>Kh√¥ng c√≥ nh√¢n vi√™n n√†o.</p>
        ) : (
          <table className="table table-bordered table-striped">
            <thead>
              <tr>
                <th style={{ width: 50 }}></th>
                <th>H·ªç t√™n</th>
                <th>T√†i kho·∫£n</th>
                <th>Ph√≤ng ban</th>
              </tr>
            </thead>
            <tbody>
              {departmentEmployees.map((emp) => (
                <tr key={emp._id}>
                  <td className="text-center">
                    <input
                      type="checkbox"
                      checked={selected.includes(emp._id)}
                      onChange={() => toggleSelect(emp._id)}
                    />
                  </td>
                  <td>{emp.name || "Kh√¥ng r√µ"}</td>
                  <td>{emp.userId?.username || "‚Äî"}</td>
                  <td>{emp.department || "‚Äî"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}

        {selected.length > 0 && (
          <button className="btn btn-primary mt-3" onClick={addToGroup}>
            ‚úî Th√™m {selected.length} nh√¢n vi√™n v√†o nh√≥m
          </button>
        )}
      </div>

      {/* =================== NH√ìM NH√ÇN VI√äN ƒêANG QU·∫¢N L√ù =================== */}
      <div className="card p-3">
        <h5>Nh√¢n vi√™n b·∫°n qu·∫£n l√Ω</h5>

        {loading ? (
          <p>ƒêang t·∫£i...</p>
        ) : group.length === 0 ? (
          <p>Ch∆∞a c√≥ nh√¢n vi√™n n√†o trong nh√≥m.</p>
        ) : (
          <table className="table table-bordered table-striped">
            <thead>
              <tr>
                <th>H·ªç t√™n</th>
                <th>T√†i kho·∫£n</th>
                <th>Ph√≤ng ban</th>
              </tr>
            </thead>
            <tbody>
              {group.map((emp) => (
                <tr key={emp._id}>
                  <td>{emp.name || "Kh√¥ng r√µ"}</td>
                  <td>{emp.userId?.username || "‚Äî"}</td>
                  <td>{emp.department || "‚Äî"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>

      <style>
        {`
          table tbody tr:hover {
            background-color: #f8f9fa;
          }
        `}
      </style>
    </UserLayout>
  );
}
