// frontend/src/pages/shared/SalaryManagement.js
import React, { useEffect, useState } from "react";
import SidebarMenu from "../../components/SidebarMenu";
import api from "../../api";

function SalaryManagement({ role }) {
  const [list, setList] = useState([]);
  const [editId, setEditId] = useState(null);
  const [form, setForm] = useState({ hourlyRate: "" });

  const load = async () => {
    try {
      if (role === "admin") {
        const res = await api.get("/admin/salary"); // l·∫•y danh s√°ch cho admin
        setList(res.data);
      } else {
        const res = await api.get("/salary/me"); // user ch·ªâ xem l∆∞∆°ng c·ªßa m√¨nh
        setList(res.data);
      }
    } catch (err) {
      console.error("‚ùå API Error:", err.response?.data || err.message);
      alert("‚ùå Kh√¥ng t·∫£i ƒë∆∞·ª£c d·ªØ li·ªáu l∆∞∆°ng");
    }
  };

  useEffect(() => { load(); }, [role]);

  const startEdit = (s) => {
    setEditId(s._id);
    setForm({ hourlyRate: s.hourlyRate });
  };

  const saveEdit = async (id) => {
    try {
      await api.put(`/admin/salary/${id}`, form);
      alert("‚úÖ C·∫≠p nh·∫≠t ti·ªÅn gi·ªù th√†nh c√¥ng");
      setEditId(null);
      setForm({ hourlyRate: "" });
      load();
    } catch (err) {
      console.error("‚ùå Update Error:", err.response?.data || err.message);
      alert("‚ùå L·ªói c·∫≠p nh·∫≠t l∆∞∆°ng");
    }
  };

  const cancelEdit = () => {
    setEditId(null);
    setForm({ hourlyRate: "" });
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3">
          <SidebarMenu role={role === "admin" ? "admin" : "user"} />
        </div>
        <div className="col-9">
          <h3>üí∞ Qu·∫£n l√Ω l∆∞∆°ng ({role})</h3>
          <table className="table table-bordered">
            <thead>
              <tr>
                {role === "admin" && <th>Nh√¢n vi√™n</th>}
                <th>T·ªïng gi·ªù l√†m</th>
                <th>Ti·ªÅn gi·ªù (VND)</th>
                <th>L∆∞∆°ng (VND)</th>
                {role === "admin" && <th>H√†nh ƒë·ªông</th>}
              </tr>
            </thead>
            <tbody>
              {list.map((s) => (
                <tr key={s._id}>
                  {role === "admin" && <td>{s.username} ({s.name})</td>}
                  <td>{s.totalHours || 0}</td>
                  <td>
                    {role === "admin" && editId === s._id ? (
                      <input
                        type="number"
                        className="form-control"
                        value={form.hourlyRate}
                        onChange={(e) => setForm({ ...form, hourlyRate: e.target.value })}
                      />
                    ) : (
                      s.hourlyRate?.toLocaleString()
                    )}
                  </td>
                  <td>{(s.amount || 0).toLocaleString()}</td>
                  {role === "admin" && (
                    <td>
                      {editId === s._id ? (
                        <>
                          <button
                            className="btn btn-success btn-sm me-2"
                            onClick={() => saveEdit(s._id)}
                          >
                            L∆∞u
                          </button>
                          <button
                            className="btn btn-secondary btn-sm"
                            onClick={cancelEdit}
                          >
                            H·ªßy
                          </button>
                        </>
                      ) : (
                        <button
                          className="btn btn-warning btn-sm"
                          onClick={() => startEdit(s)}
                        >
                          S·ª≠a
                        </button>
                      )}
                    </td>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default SalaryManagement;
