// src/pages/admin/DocumentManagementAdmin.js
import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

function DocumentManagementAdmin() {
  const [docs, setDocs] = useState([]);
  const [form, setForm] = useState({ title: "", department: "general", file: null });

  // üîπ T·∫£i danh s√°ch t√†i li·ªáu
  const load = async () => {
    try {
      const res = await api.get("/admin/documents");
      setDocs(res.data);
    } catch (err) {
      console.error("‚ùå L·ªói t·∫£i danh s√°ch:", err);
      alert("Kh√¥ng th·ªÉ t·∫£i danh s√°ch t√†i li·ªáu!");
    }
  };

  useEffect(() => {
    load();
  }, []);

  // üîπ Upload t√†i li·ªáu
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const fd = new FormData();
      fd.append("title", form.title);
      fd.append("department", form.department);
      fd.append("file", form.file);

      await api.post("/admin/documents", fd, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert("‚úÖ Upload th√†nh c√¥ng");
      setForm({ title: "", department: "general", file: null });
      load();
    } catch (err) {
      console.error("‚ùå Upload l·ªói:", err);
      alert("‚ùå L·ªói upload t√†i li·ªáu");
    }
  };

  // üîπ X√≥a t√†i li·ªáu
  const handleDelete = async (id) => {
    if (!window.confirm("B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a t√†i li·ªáu n√†y?")) return;
    try {
      await api.delete(`/admin/documents/${id}`);
      alert("üóëÔ∏è ƒê√£ x√≥a t√†i li·ªáu");
      load();
    } catch (err) {
      console.error("‚ùå X√≥a l·ªói:", err);
      alert("Kh√¥ng th·ªÉ x√≥a t√†i li·ªáu");
    }
  };

  // üîπ T·∫£i t√†i li·ªáu (gi·ªØ token)
  const handleDownload = async (id) => {
    try {
      const token = localStorage.getItem("token");
      const url = `http://localhost:5000/api/admin/documents/download/${id}`;

      const response = await fetch(url, {
        method: "GET",
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!response.ok) {
        const err = await response.json();
        throw new Error(err.error || "T·∫£i file th·∫•t b·∫°i");
      }

      // T·∫°o link download
      const blob = await response.blob();
      const a = document.createElement("a");
      a.href = window.URL.createObjectURL(blob);
      a.download = "document";
      a.click();
    } catch (err) {
      console.error("‚ùå L·ªói t·∫£i file:", err);
      alert(`Kh√¥ng th·ªÉ t·∫£i file: ${err.message}`);
    }
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3">
          <SidebarMenu role="admin" />
        </div>

        <div className="col-9">
          <h3>üìÇ Qu·∫£n l√Ω t√†i li·ªáu</h3>

          {/* Form Upload */}
          <form onSubmit={handleSubmit} className="mb-3">
            <input
              type="text"
              placeholder="Ti√™u ƒë·ªÅ"
              className="form-control mb-2"
              value={form.title}
              onChange={(e) => setForm({ ...form, title: e.target.value })}
              required
            />
            <select
              className="form-control mb-2"
              value={form.department}
              onChange={(e) => setForm({ ...form, department: e.target.value })}
            >
              <option value="general">Chung</option>
              <option value="IT">IT</option>
              <option value="HR">HR</option>
              <option value="Finance">Finance</option>
            </select>
            <input
              type="file"
              className="form-control mb-2"
              onChange={(e) => setForm({ ...form, file: e.target.files[0] })}
              required
            />
            <button className="btn btn-primary">‚ûï Upload</button>
          </form>

          {/* Danh s√°ch t√†i li·ªáu */}
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Ti√™u ƒë·ªÅ</th>
                <th>Ph√≤ng ban</th>
                <th>Ng∆∞·ªùi t·∫£i l√™n</th>
                <th>H√†nh ƒë·ªông</th>
              </tr>
            </thead>
            <tbody>
              {docs.map((d) => (
                <tr key={d._id}>
                  <td>{d.title}</td>
                  <td>{d.department}</td>
                  <td>{d.uploadedBy?.username}</td>
                  <td>
                    <button
                      onClick={() => handleDownload(d._id)}
                      className="btn btn-sm btn-success me-2"
                    >
                      ‚¨áÔ∏è T·∫£i
                    </button>
                    <button
                      onClick={() => handleDelete(d._id)}
                      className="btn btn-sm btn-danger"
                    >
                      üóë X√≥a
                    </button>
                  </td>
                </tr>
              ))}
              {docs.length === 0 && (
                <tr>
                  <td colSpan="4" className="text-center text-muted">
                    Kh√¥ng c√≥ t√†i li·ªáu n√†o
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default DocumentManagementAdmin;
