import React, { useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import { Link } from "react-router-dom";

function Dashboard() {
  const { user } = useContext(AuthContext);

  return (
    <div className="container mt-5">
      <h2>Xin chào, {user?.name}</h2>
      <p>Vai trò: {user?.role}</p>

      {user?.role === "admin" && (
        <>
          <h4>Quản lý Admin</h4>
          <ul>
            <li><Link to="/employees">Quản lý nhân viên</Link></li>
            <li><Link to="/attendance">Quản lý chấm công</Link></li>
            <li><Link to="/leave-requests">Quản lý nghỉ phép</Link></li>
            <li><Link to="/messages">Quản lý tin nhắn</Link></li>
          </ul>
        </>
      )}

      {user?.role === "employee" && (
        <>
          <h4>Chức năng Nhân viên</h4>
          <ul>
            <li><Link to="/attendance">Chấm công</Link></li>
            <li><Link to="/leave-requests">Xin nghỉ phép</Link></li>
            <li><Link to="/messages">Nhắn tin</Link></li>
          </ul>
        </>
      )}

      {user?.role === "manager" && (
        <>
          <h4>Chức năng Quản lý</h4>
          <ul>
            <li><Link to="/employees">Xem danh sách nhân viên</Link></li>
            <li><Link to="/attendance">Theo dõi chấm công</Link></li>
            <li><Link to="/leave-requests">Duyệt nghỉ phép</Link></li>
          </ul>
        </>
      )}
    </div>
  );
}

export default Dashboard;
