import React, { useState } from "react";
import api from "../api/axios";
import Layout from "../components/Layout";

export default function LeaveRequests() {
  const [form, setForm] = useState({ employeeId:"", from:"", to:"", type:"nghỉ phép" });
  const [msg, setMsg] = useState("");

  const handleChange = e => setForm({...form, [e.target.name]: e.target.value});

  const handleSubmit = async e => {
    e.preventDefault();
    await api.post("/leave-requests", form);
    setMsg("Đã gửi đơn nghỉ phép!");
  };

  return (
    <Layout title="Đơn nghỉ phép">
      <form className="row g-2" onSubmit={handleSubmit}>
        <div className="col"><input className="form-control" name="employeeId" placeholder="EmployeeId" onChange={handleChange}/></div>
        <div className="col"><input type="date" className="form-control" name="from" onChange={handleChange}/></div>
        <div className="col"><input type="date" className="form-control" name="to" onChange={handleChange}/></div>
        <div className="col">
          <select className="form-select" name="type" onChange={handleChange}>
            <option value="nghỉ phép">Nghỉ phép</option>
            <option value="nghỉ ốm">Nghỉ ốm</option>
            <option value="khác">Khác</option>
          </select>
        </div>
        <div className="col-2 d-grid"><button className="btn btn-primary">Gửi</button></div>
      </form>
      <p>{msg}</p>
    </Layout>
  );
}
