// frontend/src/pages/user/DocumentManagementUser.js
import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

export default function DocumentManagementUser() {
  const [docs, setDocs] = useState([]);
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [loading, setLoading] = useState(false);

  // üß© Load t√†i li·ªáu
  const loadDocuments = async () => {
    try {
      const res = await api.get("/employees/documents");
      setDocs(res.data || []);
    } catch (err) {
      console.error("‚ùå L·ªói load t√†i li·ªáu:", err);
      alert("Kh√¥ng th·ªÉ t·∫£i danh s√°ch t√†i li·ªáu");
    }
  };

  useEffect(() => {
    loadDocuments();
  }, []);

  // üì§ Upload file
  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) return alert("Vui l√≤ng ch·ªçn file ƒë·ªÉ t·∫£i l√™n!");
    setLoading(true);
    try {
      const formData = new FormData();
      formData.append("file", file);
      formData.append("title", title);
      formData.append("description", description);

      await api.post("/employees/documents/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("‚úÖ T·∫£i t√†i li·ªáu l√™n th√†nh c√¥ng!");
      setFile(null);
      setTitle("");
      setDescription("");
      await loadDocuments();
    } catch (err) {
      console.error("‚ùå Upload error:", err);
      alert(err.response?.data?.error || "L·ªói khi t·∫£i t√†i li·ªáu");
    } finally {
      setLoading(false);
    }
  };

  // üì• Download file
  const handleDownload = async (doc) => {
    try {
      const base = process.env.REACT_APP_API_BASE || "http://localhost:5000/api";
      window.open(`${base}/employees/documents/download/${doc._id}`, "_blank");
    } catch (err) {
      console.error("‚ùå Download error:", err);
      alert("Kh√¥ng th·ªÉ t·∫£i t√†i li·ªáu n√†y");
    }
  };

  // üóëÔ∏è Delete file
  const handleDelete = async (id) => {
    if (!window.confirm("B·∫°n c√≥ ch·∫Øc ch·∫Øn mu·ªën x√≥a t√†i li·ªáu n√†y?")) return;
    try {
      await api.delete(`/employees/documents/${id}`);
      alert("üóëÔ∏è X√≥a th√†nh c√¥ng!");
      await loadDocuments();
    } catch (err) {
      console.error("‚ùå Delete error:", err);
      alert(err.response?.data?.error || "Kh√¥ng th·ªÉ x√≥a t√†i li·ªáu");
    }
  };

  return (
    <div className="container-fluid mt-3">
      <div className="row">
        {/* Sidebar */}
        <div className="col-3">
          <SidebarMenu role="user" />
        </div>

        {/* Main content */}
        <div className="col-9">
          <h3 className="mb-3">üìÇ T√†i li·ªáu ph√≤ng ban c·ªßa t√¥i</h3>

          {/* Upload Section */}
          <div className="card mb-4">
            <div className="card-header bg-light">
              <strong>‚¨ÜÔ∏è T·∫£i t√†i li·ªáu l√™n</strong>
            </div>
            <div className="card-body">
              <form onSubmit={handleUpload}>
                <div className="mb-2">
                  <label className="form-label">Ti√™u ƒë·ªÅ</label>
                  <input
                    type="text"
                    className="form-control"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    placeholder="Nh·∫≠p ti√™u ƒë·ªÅ t√†i li·ªáu"
                    required
                  />
                </div>
                <div className="mb-2">
                  <label className="form-label">M√¥ t·∫£ (t√πy ch·ªçn)</label>
                  <textarea
                    className="form-control"
                    rows="2"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                  />
                </div>
                <div className="mb-2">
                  <label className="form-label">Ch·ªçn file</label>
                  <input
                    type="file"
                    className="form-control"
                    onChange={(e) => setFile(e.target.files[0])}
                    required
                  />
                </div>
                <button className="btn btn-primary" disabled={loading}>
                  {loading ? "ƒêang t·∫£i l√™n..." : "T·∫£i l√™n"}
                </button>
              </form>
            </div>
          </div>

          {/* Document Table */}
          <div className="card">
            <div className="card-header bg-light">
              <strong>üìë Danh s√°ch t√†i li·ªáu</strong>
            </div>
            <div className="card-body p-0">
              <table className="table table-bordered mb-0">
                <thead className="table-light">
                  <tr>
                    <th>Ti√™u ƒë·ªÅ</th>
                    <th>Ph√≤ng ban</th>
                    <th>Ng∆∞·ªùi t·∫£i l√™n</th>
                    <th>Ng√†y t·∫£i</th>
                    <th>H√†nh ƒë·ªông</th>
                  </tr>
                </thead>
                <tbody>
                  {docs.length === 0 ? (
                    <tr>
                      <td colSpan={5} className="text-center text-muted">
                        Ch∆∞a c√≥ t√†i li·ªáu
                      </td>
                    </tr>
                  ) : (
                    docs.map((d) => (
                      <tr key={d._id}>
                        <td>{d.title}</td>
                        <td>{d.department}</td>
                        <td>{d.uploadedBy?.username || "N/A"}</td>
                        <td>
                          {d.uploadedAt
                            ? new Date(d.uploadedAt).toLocaleString("vi-VN")
                            : "--"}
                        </td>
                        <td className="text-nowrap">
                          <button
                            className="btn btn-sm btn-success me-2"
                            onClick={() => handleDownload(d)}
                          >
                            ‚¨áÔ∏è T·∫£i
                          </button>
                          {d.uploadedBy?._id ===
                            JSON.parse(localStorage.getItem("user"))?.user?._id && (
                            <button
                              className="btn btn-sm btn-danger"
                              onClick={() => handleDelete(d._id)}
                            >
                              üóëÔ∏è X√≥a
                            </button>
                          )}
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
