import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

function Attendance() {
  const [records, setRecords] = useState([]);
  const [todayRecord, setTodayRecord] = useState(null);
  const [loading, setLoading] = useState(true);

  // === Load d·ªØ li·ªáu ch·∫•m c√¥ng ===
  const load = async () => {
    try {
      const res = await api.get("/attendance");
      setRecords(res.data);

      const today = new Date().toISOString().split("T")[0];
      const todayRec = res.data.find((r) => r.date === today);
      setTodayRecord(todayRec || null);
    } catch (err) {
      console.error("‚ùå L·ªói load ch·∫•m c√¥ng:", err);
      alert("L·ªói t·∫£i d·ªØ li·ªáu ch·∫•m c√¥ng!");
    } finally {
      setLoading(false);
    }
  };

  // === Check-in ===
  const checkIn = async () => {
    try {
      await api.post("/attendance/check-in");
      alert("‚úÖ Check-in th√†nh c√¥ng");
      load();
    } catch (err) {
      alert(err.response?.data?.error || "‚ùå L·ªói check-in");
    }
  };

  // === Check-out ===
  const checkOut = async () => {
    try {
      await api.post("/attendance/check-out");
      alert("‚úÖ Check-out th√†nh c√¥ng");
      load();
    } catch (err) {
      alert(err.response?.data?.error || "‚ùå L·ªói check-out");
    }
  };

  useEffect(() => {
    load();
  }, []);

  // === UI ===
  return (
    <div className="container mt-3">
      <div className="row">
        {/* Sidebar */}
        <div className="col-3">
          <SidebarMenu role="employee" />
        </div>

        {/* N·ªôi dung ch√≠nh */}
        <div className="col-9">
          <h3>üïí Ch·∫•m c√¥ng</h3>

          {/* Tr·∫°ng th√°i h√¥m nay */}
          {todayRecord ? (
            <div className="alert alert-info">
              <strong>H√¥m nay:</strong> {todayRecord.date} <br />
              ‚è∞ Check-in:{" "}
              {todayRecord.checkIn
                ? new Date(todayRecord.checkIn).toLocaleTimeString()
                : "‚Äî"}
              <br />
              üïì Check-out:{" "}
              {todayRecord.checkOut
                ? new Date(todayRecord.checkOut).toLocaleTimeString()
                : "‚Äî"}
              <br />
              ‚è≥ T·ªïng gi·ªù:{" "}
              {todayRecord.totalHours ? todayRecord.totalHours.toFixed(2) : 0} gi·ªù
            </div>
          ) : (
            <p>üìÖ B·∫°n ch∆∞a ch·∫•m c√¥ng h√¥m nay.</p>
          )}

          {/* N√∫t ch·∫•m c√¥ng */}
          <div className="mb-3">
            <button
              className="btn btn-success me-2"
              onClick={checkIn}
              disabled={!!todayRecord?.checkIn}
            >
              ‚úÖ Check-in
            </button>

            <button
              className="btn btn-danger"
              onClick={checkOut}
              disabled={!todayRecord?.checkIn || !!todayRecord?.checkOut}
            >
              ‚èπ Check-out
            </button>
          </div>

          {/* B·∫£ng l·ªãch s·ª≠ */}
          {loading ? (
            <p>‚è≥ ƒêang t·∫£i d·ªØ li·ªáu...</p>
          ) : records.length === 0 ? (
            <p>Kh√¥ng c√≥ d·ªØ li·ªáu ch·∫•m c√¥ng.</p>
          ) : (
            <table className="table table-bordered">
              <thead>
                <tr>
                  <th>Ng√†y</th>
                  <th>Gi·ªù v√†o</th>
                  <th>Gi·ªù ra</th>
                  <th>T·ªïng gi·ªù</th>
                </tr>
              </thead>
              <tbody>
                {records.map((r) => (
                  <tr key={r._id}>
                    <td>{r.date}</td>
                    <td>
                      {r.checkIn ? new Date(r.checkIn).toLocaleTimeString() : "‚Äî"}
                    </td>
                    <td>
                      {r.checkOut ? new Date(r.checkOut).toLocaleTimeString() : "‚Äî"}
                    </td>
                    <td>{r.totalHours?.toFixed(2) || 0}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>
    </div>
  );
}

export default Attendance;
