import React, { useEffect, useState } from "react";
import AdminLayout from "../../layouts/AdminLayout";
import api from "../../api";

export default function AttendanceManagementAdmin() {
  const [list, setList] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [modalData, setModalData] = useState(null);

  const load = async () => {
    try {
      const res = await api.get("/attendance");
      setList(res.data);
      const emp = await api.get("/employees");
      setEmployees(emp.data);
    } catch (err) {
      alert("Kh√¥ng t·∫£i ƒë∆∞·ª£c d·ªØ li·ªáu");
    }
  };

  useEffect(() => {
    load();
  }, []);

  const fmtTime = (t) =>
    t ? new Date(t).toLocaleTimeString("vi-VN", { hour12: false }) : "";

  const fmtDate = (d) =>
    d ? new Date(d).toLocaleDateString("vi-VN") : "";

  // ============================
  // Th√™m ho·∫∑c s·ª≠a ch·∫•m c√¥ng
  // ============================
  const saveAttendance = async () => {
    try {
      if (!modalData.userId || !modalData.date) {
        alert("Thi·∫øu user ho·∫∑c ng√†y");
        return;
      }

      if (modalData._id) {
        // UPDATE
        await api.put(`/attendance/manual/${modalData._id}`, modalData);
        alert("ƒê√£ c·∫≠p nh·∫≠t");
      } else {
        // CREATE
        await api.post("/attendance/manual", modalData);
        alert("ƒê√£ th√™m");
      }

      setModalData(null);
      load();
    } catch (err) {
      alert("L·ªói l∆∞u ch·∫•m c√¥ng");
    }
  };

  // ============================
  // X√≥a b·∫£n ghi
  // ============================
  const remove = async (id) => {
    if (!window.confirm("B·∫°n ch·∫Øc ch·∫Øn mu·ªën xo√°?")) return;
    try {
      await api.delete(`/attendance/manual/${id}`);
      load();
    } catch (err) {
      alert("Kh√¥ng xo√° ƒë∆∞·ª£c");
    }
  };

  return (
    <AdminLayout>
      <h2 className="mb-3">‚è± Qu·∫£n l√Ω ch·∫•m c√¥ng</h2>

      {/* N√∫t th√™m */}
      <button
        className="btn btn-primary mb-3"
        onClick={() => setModalData({})}
      >
        ‚ûï Th√™m ch·∫•m c√¥ng
      </button>

      {/* B·∫£ng */}
      <div className="card p-3">
        <table className="table table-bordered table-striped">
          <thead>
            <tr>
              <th>Nh√¢n vi√™n</th>
              <th>Ng√†y</th>
              <th>V√†o</th>
              <th>Ra</th>
              <th>Tr·ªÖ (ph√∫t)</th>
              <th>OT (gi·ªù)</th>
              <th>Ng√†y c√¥ng</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {list.map((a) => (
              <tr key={a._id}>
                <td>{a.userId?.username}</td>
                <td>{fmtDate(a.date)}</td>
                <td>{fmtTime(a.checkIn)}</td>
                <td>{fmtTime(a.checkOut)}</td>
                <td>{a.lateMinutes}</td>
                <td>{a.overtimeHours}</td>
                <td>{a.totalDays}</td>
                <td className="text-nowrap">
                  <button
                    className="btn btn-sm btn-warning me-2"
                    onClick={() => setModalData(a)}
                  >
                    ‚úè S·ª≠a
                  </button>
                  <button
                    className="btn btn-sm btn-danger"
                    onClick={() => remove(a._id)}
                  >
                    üóë X√≥a
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* ===================== MODAL ===================== */}
      {modalData && (
        <div className="modal fade show" style={{ display: "block" }}>
          <div className="modal-dialog">
            <div className="modal-content">

              <div className="modal-header">
                <h5 className="modal-title">
                  {modalData._id ? "S·ª≠a ch·∫•m c√¥ng" : "Th√™m ch·∫•m c√¥ng"}
                </h5>

                <button
                  className="btn-close"
                  onClick={() => setModalData(null)}
                />
              </div>

              <div className="modal-body">

                {/* User */}
                <label>Nh√¢n vi√™n</label>
                <select
                  className="form-select mb-2"
                  value={modalData.userId || ""}
                  onChange={(e) =>
                    setModalData({ ...modalData, userId: e.target.value })
                  }
                >
                  <option value="">-- Ch·ªçn --</option>
                  {employees.map((emp) => (
                    <option key={emp._id} value={emp.userId?._id}>
                      {emp.userId?.username}
                    </option>
                  ))}
                </select>

                {/* Ng√†y */}
                <label>Ng√†y</label>
                <input
                  type="date"
                  className="form-control mb-2"
                  value={modalData.date || ""}
                  onChange={(e) =>
                    setModalData({ ...modalData, date: e.target.value })
                  }
                />

                {/* Check-in */}
                <label>Gi·ªù v√†o</label>
                <input
                  type="datetime-local"
                  className="form-control mb-2"
                  value={
                    modalData.checkIn
                      ? new Date(modalData.checkIn).toISOString().slice(0, 16)
                      : ""
                  }
                  onChange={(e) =>
                    setModalData({ ...modalData, checkIn: e.target.value })
                  }
                />

                {/* Check-out */}
                <label>Gi·ªù ra</label>
                <input
                  type="datetime-local"
                  className="form-control mb-2"
                  value={
                    modalData.checkOut
                      ? new Date(modalData.checkOut)
                          .toISOString()
                          .slice(0, 16)
                      : ""
                  }
                  onChange={(e) =>
                    setModalData({ ...modalData, checkOut: e.target.value })
                  }
                />

                {/* ƒêi tr·ªÖ */}
                <label>ƒêi tr·ªÖ (ph√∫t)</label>
                <input
                  type="number"
                  className="form-control mb-2"
                  value={modalData.lateMinutes || 0}
                  onChange={(e) =>
                    setModalData({
                      ...modalData,
                      lateMinutes: Number(e.target.value),
                    })
                  }
                />

                {/* OT */}
                <label>TƒÉng ca (gi·ªù)</label>
                <input
                  type="number"
                  className="form-control mb-2"
                  value={modalData.overtimeHours || 0}
                  onChange={(e) =>
                    setModalData({
                      ...modalData,
                      overtimeHours: Number(e.target.value),
                    })
                  }
                />

                {/* Ng√†y c√¥ng */}
                <label>Ng√†y c√¥ng</label>
                <input
                  type="number"
                  className="form-control mb-2"
                  value={modalData.totalDays || 1}
                  onChange={(e) =>
                    setModalData({
                      ...modalData,
                      totalDays: Number(e.target.value),
                    })
                  }
                />
              </div>

              <div className="modal-footer">
                <button
                  className="btn btn-secondary"
                  onClick={() => setModalData(null)}
                >
                  ƒê√≥ng
                </button>

                <button className="btn btn-primary" onClick={saveAttendance}>
                  L∆∞u
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </AdminLayout>
  );
}
