import React, { useState } from "react";
import axios from "axios";

function LeaveRequest() {
  const [reason, setReason] = useState("");
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const token = localStorage.getItem("token");

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post("http://localhost:5000/api/leave-requests", {
      reason, fromDate, toDate
    }, { headers: { Authorization: `Bearer ${token}` } });
    alert("Đã gửi đơn nghỉ phép!");
  };

  return (
    <div className="container">
      <h3>Xin nghỉ phép</h3>
      <form onSubmit={handleSubmit}>
        <input className="form-control mb-2" placeholder="Lý do" value={reason} onChange={(e) => setReason(e.target.value)} />
        <input className="form-control mb-2" type="date" value={fromDate} onChange={(e) => setFromDate(e.target.value)} />
        <input className="form-control mb-2" type="date" value={toDate} onChange={(e) => setToDate(e.target.value)} />
        <button className="btn btn-primary">Gửi yêu cầu</button>
      </form>
    </div>
  );
}

export default LeaveRequest;
