import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Login() {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.post("http://localhost:5000/api/auth/login", {
        username,
        password,
      });

      // ‚úÖ L∆∞u token & th√¥ng tin user v√†o localStorage
      localStorage.setItem(
        "authUser",
        JSON.stringify({
          token: res.data.token,
          user: res.data.user, // { id, username, role }
        })
      );

      // üìå Chuy·ªÉn h∆∞·ªõng theo role
      const role = res.data.user.role;
      if (role === "admin") {
        navigate("/admin/dashboard");
      } else if (role === "manager" || role === "employee") {
        navigate("/user/dashboard");
      } else {
        alert("T√†i kho·∫£n c·ªßa b·∫°n kh√¥ng c√≥ quy·ªÅn truy c·∫≠p h·ªá th·ªëng n√†y.");
      }
    } catch (err) {
      console.error("‚ùå L·ªói ƒëƒÉng nh·∫≠p:", err);
      alert(
        err.response?.data?.error || "Sai t√™n ƒëƒÉng nh·∫≠p ho·∫∑c m·∫≠t kh·∫©u!"
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mt-5" style={{ maxWidth: 400 }}>
      <h3>üîê ƒêƒÉng nh·∫≠p</h3>
      <form onSubmit={handleLogin}>
        <div className="mb-3">
          <label>T√™n ƒëƒÉng nh·∫≠p</label>
          <input
            type="text"
            className="form-control"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label>M·∫≠t kh·∫©u</label>
          <input
            type="password"
            className="form-control"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary w-100" disabled={loading}>
          {loading ? "ƒêang ƒëƒÉng nh·∫≠p..." : "ƒêƒÉng nh·∫≠p"}
        </button>
      </form>
    </div>
  );
}

export default Login;
