// frontend/src/pages/user/ProfileUpdate.js
import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

export default function ProfileUpdate() {
  const [profile, setProfile] = useState(null);
  const [name, setName] = useState("");
  const [department, setDepartment] = useState("");
  const [position, setPosition] = useState("");
  const [loading, setLoading] = useState(false);

  // üß© L·∫•y th√¥ng tin hi·ªán t·∫°i
  const loadProfile = async () => {
    try {
      const res = await api.get("/employees/me"); // ‚úÖ route ch√≠nh x√°c
      setProfile(res.data);
      setName(res.data.name || "");
      setDepartment(res.data.department || "");
      setPosition(res.data.position || "");
    } catch (err) {
      console.error("‚ùå L·ªói t·∫£i h·ªì s∆°:", err);
      alert("Kh√¥ng th·ªÉ t·∫£i th√¥ng tin c√° nh√¢n!");
    }
  };

  useEffect(() => {
    loadProfile();
  }, []);

  // üíæ L∆∞u c·∫≠p nh·∫≠t
  const save = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await api.put("/employees/profile", { name, department, position });
      alert("‚úÖ C·∫≠p nh·∫≠t th√¥ng tin th√†nh c√¥ng!");
      await loadProfile();
    } catch (err) {
      console.error("‚ùå L·ªói c·∫≠p nh·∫≠t:", err);
      alert(err.response?.data?.error || "Kh√¥ng th·ªÉ c·∫≠p nh·∫≠t th√¥ng tin!");
    } finally {
      setLoading(false);
    }
  };

  if (!profile) return <div className="p-3 text-center">ƒêang t·∫£i th√¥ng tin...</div>;

  return (
    <div className="container-fluid mt-3">
      <div className="row">
        {/* Sidebar */}
        <div className="col-3">
          <SidebarMenu role="user" />
        </div>

        {/* Main content */}
        <div className="col-9">
          <h3 className="mb-3">üë§ C·∫≠p nh·∫≠t th√¥ng tin c√° nh√¢n</h3>

          <div className="card">
            <div className="card-body">
              <form onSubmit={save}>
                <div className="mb-3">
                  <label className="form-label">T√™n hi·ªÉn th·ªã</label>
                  <input
                    type="text"
                    className="form-control"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                  />
                </div>

                <div className="mb-3">
                  <label className="form-label">Ph√≤ng ban</label>
                  <input
                    type="text"
                    className="form-control"
                    value={department}
                    onChange={(e) => setDepartment(e.target.value)}
                    required
                  />
                </div>

                <div className="mb-3">
                  <label className="form-label">V·ªã tr√≠ c√¥ng vi·ªác</label>
                  <input
                    type="text"
                    className="form-control"
                    value={position}
                    onChange={(e) => setPosition(e.target.value)}
                  />
                </div>

                <button className="btn btn-primary" disabled={loading}>
                  {loading ? "ƒêang l∆∞u..." : "üíæ L∆∞u thay ƒë·ªïi"}
                </button>
              </form>
            </div>
          </div>

          {/* Th√¥ng tin t√†i kho·∫£n */}
          <div className="card mt-4">
            <div className="card-header bg-light">
              <strong>Th√¥ng tin t√†i kho·∫£n</strong>
            </div>
            <div className="card-body">
              <p>
                <strong>T√™n ƒëƒÉng nh·∫≠p:</strong> {profile.userId?.username}
              </p>
              <p>
                <strong>Ch·ª©c v·ª•:</strong> {profile.userId?.role}
              </p>
              <p>
                <strong>Ng√†y t·∫°o:</strong>{" "}
                {new Date(profile.createdAt).toLocaleString("vi-VN")}
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
