import React, { useEffect, useRef, useState, useContext } from "react";
import { io } from "socket.io-client";
import SidebarMenu from "../../components/SidebarMenu";
import api from "../../api";
import { AuthContext } from "../../context/AuthContext";

const socket = io("http://localhost:5000", { transports: ["websocket"] });

function Chat() {
  const { user } = useContext(AuthContext);
  const [to, setTo] = useState("");
  const [content, setContent] = useState("");
  const [messages, setMessages] = useState([]);
  const bottomRef = useRef();

  const load = async () => {
    if (!to) return setMessages([]);
    const res = await api.get(`/messages/${to}`);
    setMessages(res.data);
  };

  useEffect(() => { load(); }, [to]);

  useEffect(() => {
    socket.on("receive_message", () => load());
    return () => socket.off("receive_message");
  }, [to]);

  const send = async (e) => {
    e.preventDefault();
    if (!to || !content) return;
    await api.post("/messages", { to, content });
    socket.emit("send_message", { to, content, from: user.id });
    setContent("");
    load();
  };

  useEffect(() => { bottomRef.current?.scrollIntoView({ behavior: "smooth" }); }, [messages]);

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="user" /></div>
        <div className="col-9">
          <h3>ðŸ’¬ Chat</h3>
          <form className="mb-2" onSubmit={send}>
            <input className="form-control mb-2" placeholder="Nháº­p UserID ngÆ°á»i nháº­n" value={to} onChange={(e)=>setTo(e.target.value)} />
            <div className="input-group">
              <input className="form-control" placeholder="Ná»™i dung..." value={content} onChange={(e)=>setContent(e.target.value)} />
              <button className="btn btn-primary">Gá»­i</button>
            </div>
          </form>
          <div className="border rounded p-3" style={{minHeight: 240, maxHeight: 360, overflowY:"auto"}}>
            {messages.map(m => (
              <div key={m._id} className={`mb-2 ${m.from?._id===user.id?"text-end":""}`}>
                <div><small>{m.from?.username} âžœ {m.to?.username}</small></div>
                <div>{m.content}</div>
              </div>
            ))}
            <div ref={bottomRef} />
          </div>
        </div>
      </div>
    </div>
  );
}
export default Chat;
