import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Register() {
  const [formData, setFormData] = useState({
    username: "",
    password: "",
    name: "",
    department: "",
    position: "",
    role: "employee"
  });
  const navigate = useNavigate();

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/auth/register", formData);
      alert("Đăng ký thành công!");
      navigate("/");
    } catch (err) {
      alert(err.response?.data?.error || "Lỗi đăng ký");
    }
  };

  return (
    <div className="container mt-5" style={{ maxWidth: "500px" }}>
      <h2>Đăng ký tài khoản</h2>
      <form onSubmit={handleSubmit}>
        <input className="form-control mb-2" name="username" placeholder="Tên đăng nhập"
          value={formData.username} onChange={handleChange} />
        <input className="form-control mb-2" type="password" name="password" placeholder="Mật khẩu"
          value={formData.password} onChange={handleChange} />
        <input className="form-control mb-2" name="name" placeholder="Họ và tên"
          value={formData.name} onChange={handleChange} />
        <input className="form-control mb-2" name="department" placeholder="Phòng ban"
          value={formData.department} onChange={handleChange} />
        <input className="form-control mb-2" name="position" placeholder="Chức vụ"
          value={formData.position} onChange={handleChange} />
        <select className="form-control mb-3" name="role" value={formData.role} onChange={handleChange}>
          <option value="employee">Nhân viên</option>
          <option value="manager">Quản lý</option>
          <option value="admin">Admin</option>
        </select>
        <button className="btn btn-success w-100">Đăng ký</button>
      </form>
    </div>
  );
}

export default Register;
