import React, { useEffect, useState } from "react";
import api from "../api/axios";
import Layout from "../components/Layout";

export default function Employees() {
  const [list, setList] = useState([]);
  const [form, setForm] = useState({ userId:"", name:"", department:"", position:"" });

  const fetchData = async () => {
    const { data } = await api.get("/employees");
    setList(data);
  };

  useEffect(()=>{ fetchData(); }, []);

  const handleChange = e => setForm({...form, [e.target.name]: e.target.value});

  const handleSubmit = async e => {
    e.preventDefault();
    await api.post("/employees", form);
    fetchData();
  };

  return (
    <Layout title="Quản lý nhân viên">
      <form className="row g-2 mb-3" onSubmit={handleSubmit}>
        <div className="col"><input className="form-control" placeholder="UserId" name="userId" onChange={handleChange}/></div>
        <div className="col"><input className="form-control" placeholder="Họ tên" name="name" onChange={handleChange}/></div>
        <div className="col"><input className="form-control" placeholder="Phòng ban" name="department" onChange={handleChange}/></div>
        <div className="col"><input className="form-control" placeholder="Chức vụ" name="position" onChange={handleChange}/></div>
        <div className="col-2 d-grid"><button className="btn btn-success">Thêm</button></div>
      </form>

      <table className="table table-bordered">
        <thead><tr><th>Tên</th><th>Phòng</th><th>Chức vụ</th></tr></thead>
        <tbody>{list.map(e=><tr key={e._id}><td>{e.name}</td><td>{e.department}</td><td>{e.position}</td></tr>)}</tbody>
      </table>
    </Layout>
  );
}
