import React, { useEffect, useState } from "react";
import SidebarMenu from "../../components/SidebarMenu";
import api from "../../api";
import { DEPARTMENTS, POSITIONS, ROLES } from "../../config/constants";

function HRManagement() {
  const [list, setList] = useState([]);
  const [form, setForm] = useState({
    username: "",
    password: "",
    name: "",
    department: "",
    position: "",
    role: "employee",
  });

  const load = async () => {
    try {
      const res = await api.get("/admin/employees");
      setList(res.data);
    } catch (err) {
      alert("L·ªói t·∫£i danh s√°ch nh√¢n vi√™n");
    }
  };

  useEffect(() => { load(); }, []);

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post("/admin/employees", form);
      alert("Th√™m nh√¢n vi√™n th√†nh c√¥ng");
      setForm({ username: "", password: "", name: "", department: "", position: "", role: "employee" });
      load();
    } catch (err) {
      alert(err.response?.data?.error || "L·ªói th√™m nh√¢n vi√™n");
    }
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="admin" /></div>
        <div className="col-9">
          <h3>üë®‚Äçüíº Qu·∫£n l√Ω nh√¢n s·ª±</h3>

          <form onSubmit={onSubmit} className="mb-4 border p-3 rounded">
            <h5>‚ûï Th√™m nh√¢n vi√™n m·ªõi</h5>
            <div className="row g-2 mt-2">
              <div className="col"><input className="form-control" placeholder="Username"
                value={form.username} onChange={(e)=>setForm({...form,username:e.target.value})} /></div>
              <div className="col"><input className="form-control" type="password" placeholder="Password"
                value={form.password} onChange={(e)=>setForm({...form,password:e.target.value})} /></div>
            </div>

            <div className="row g-2 mt-2">
              <div className="col"><input className="form-control" placeholder="H·ªç t√™n"
                value={form.name} onChange={(e)=>setForm({...form,name:e.target.value})} /></div>
              <div className="col">
                <select className="form-control" value={form.department}
                  onChange={(e)=>setForm({...form,department:e.target.value})}>
                  <option value="">-- Ch·ªçn ph√≤ng ban --</option>
                  {DEPARTMENTS.map(d => <option key={d} value={d}>{d}</option>)}
                </select>
              </div>
              <div className="col">
                <select className="form-control" value={form.position}
                  onChange={(e)=>setForm({...form,position:e.target.value})}>
                  <option value="">-- Ch·ªçn ch·ª©c v·ª• --</option>
                  {POSITIONS.map(p => <option key={p} value={p}>{p}</option>)}
                </select>
              </div>
            </div>

            <div className="row g-2 mt-2">
              <div className="col">
                <select className="form-control" value={form.role}
                  onChange={(e)=>setForm({...form,role:e.target.value})}>
                  {ROLES.map(r => <option key={r} value={r}>{r}</option>)}
                </select>
              </div>
              <div className="col">
                <button className="btn btn-primary w-100">Th√™m nh√¢n vi√™n</button>
              </div>
            </div>
          </form>

          <h5>üìã Danh s√°ch nh√¢n vi√™n</h5>
          <table className="table table-bordered">
            <thead><tr><th>Username</th><th>Role</th><th>H·ªç t√™n</th><th>Ph√≤ng ban</th><th>Ch·ª©c v·ª•</th></tr></thead>
            <tbody>
              {list.map(e => (
                <tr key={e._id}>
                  <td>{e.userId?.username}</td>
                  <td>{e.userId?.role}</td>
                  <td>{e.name}</td>
                  <td>{e.department}</td>
                  <td>{e.position}</td>
                </tr>
              ))}
            </tbody>
          </table>

        </div>
      </div>
    </div>
  );
}

export default HRManagement;
