// frontend/src/pages/user/Chat.js
import React, { useEffect, useMemo, useRef, useState } from "react";
import io from "socket.io-client";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";
import { useAuth } from "../../context/AuthContext";

// Socket base = API_BASE b·ªè ƒëu√¥i /api
const SOCKET_BASE =
  (process.env.REACT_APP_API_BASE || "http://localhost:5000").replace("/api", "");

// D√πng m·ªôt socket d√πng chung
const socket = io(SOCKET_BASE, { transports: ["websocket"], autoConnect: true });

export default function Chat() {
  const { user } = useAuth(); // { user: {_id, username, department, ...} }
  const me = user?.user;

  const [deptRoom, setDeptRoom] = useState(null);
  const [privateRooms, setPrivateRooms] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [chatOutsideDept, setChatOutsideDept] = useState(false);

  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");

  const bottomRef = useRef();
  const joinedRoomIdRef = useRef(null);
  const myDepartment = useMemo(() => me?.department || null, [me]);

  // ====== Helpers ======
  const scrollToBottom = () =>
    setTimeout(() => bottomRef.current?.scrollIntoView({ behavior: "smooth" }), 20);

  // X√°c ƒë·ªãnh tin c·ªßa m√¨nh (∆∞u ti√™n id; thi·∫øu id th√¨ fallback username - lowercase)
  const isMine = (m) => {
    const meId = me?._id?.toString();
    const meName = (me?.username || "").toLowerCase();

    const sid = m?.sender?._id ? String(m.sender._id) : null;
    const sname = (m?.sender?.username || "").toLowerCase();

    if (sid && meId) return sid === meId;
    if (sname && meName) return sname === meName;
    return false;
  };

  // Chu·∫©n h√≥a message t·ª´ DB / socket v·ªÅ 1 format
  const normalizeMsg = (raw) => {
    const senderId =
      raw?.sender?._id ??
      raw?.fromUserId ??
      raw?.senderId ??
      null;

    const senderName =
      raw?.sender?.username ??
      raw?.fromUserName ??
      raw?.fromUsername ??
      null;

    return {
      _id: String(raw?._id || Math.random().toString(36).slice(2)),
      roomId: String(raw?.roomId || currentRoom?._id || ""),
      sender: { _id: senderId ? String(senderId) : null, username: senderName || "N/A" },
      content: String(raw?.content ?? raw?.message ?? ""),
      createdAt: raw?.createdAt || new Date().toISOString(),
    };
  };

  // ====== Bootstrap socket: join user + dept ======
  useEffect(() => {
    if (!me?._id) return;
    socket.emit("join", { userId: me._id, department: me.department });
  }, [me]);

  // ====== Load rooms & employees ======
  const loadRooms = async () => {
    try {
      const res = await api.get("/messages/rooms");
      setPrivateRooms(res.data?.privateRooms || []);
      setDeptRoom(res.data?.deptRoom || null);
    } catch (err) {
      console.error("‚ùå L·ªói load rooms:", err);
    }
  };

  const loadEmployees = async () => {
    try {
      const scope = chatOutsideDept ? "all" : "dept";
      const res = await api.get(`/employees/peers?scope=${scope}`);
      setEmployees(res.data || []);
    } catch (err) {
      console.error("‚ùå L·ªói load danh s√°ch nh√¢n vi√™n:", err);
      setEmployees([]);
    }
  };

  useEffect(() => {
    loadRooms();
  }, []);
  useEffect(() => {
    loadEmployees();
  }, [chatOutsideDept]);

  // ====== Join/Leave room khi ch·ªçn ph√≤ng ======
  const openRoom = async (room) => {
    if (joinedRoomIdRef.current) {
      socket.emit("leave_room", { roomId: joinedRoomIdRef.current });
    }
    socket.emit("join_room", { roomId: room._id });
    joinedRoomIdRef.current = room._id;

    setCurrentRoom(room);
    await loadMessages(room);
  };

  // ====== Load tin nh·∫Øn trong ph√≤ng ======
  const loadMessages = async (room) => {
    try {
      const res = await api.get(`/messages/${room._id}`);
      const list = (res.data || []).map(normalizeMsg);
      setMessages(list);
      scrollToBottom();
    } catch (err) {
      console.error("‚ùå L·ªói load tin nh·∫Øn:", err);
      setMessages([]);
    }
  };

  // ====== Socket receive realtime ======
  useEffect(() => {
    const handler = (payload) => {
      if (!currentRoom) return;
      if (payload?.roomId?.toString() !== currentRoom._id?.toString()) return;

      setMessages((prev) => [...prev, normalizeMsg(payload)]);
      scrollToBottom();
    };

    socket.on("receive_message", handler);
    return () => socket.off("receive_message", handler);
  }, [currentRoom]);

  // ====== T·∫°o private room v·ªõi 1 nh√¢n vi√™n ======
  const startPrivateWith = async (otherUserId) => {
    try {
      const res = await api.post("/messages/rooms/private", { otherUserId });
      await loadRooms();
      await openRoom(res.data);
    } catch (err) {
      console.error("‚ùå L·ªói t·∫°o private room:", err);
    }
  };

  // ====== G·ª≠i tin ======
  const sendMessage = async (e) => {
    e.preventDefault();
    if (!text.trim() || !currentRoom) return;

    const content = text.trim();

    try {
      // 1) L∆∞u DB
      const savedRes = await api.post("/messages", {
        roomId: currentRoom._id,
        content,
      });
      const savedNorm = normalizeMsg(savedRes.data);

      // √©p sender l√† m√¨nh ƒë·ªÉ cƒÉn ph·∫£i ch·∫Øc ch·∫Øn
      const myMsg = {
        ...savedNorm,
        sender: {
          _id: me?._id || savedNorm.sender?._id,
          username: me?.username || savedNorm.sender?.username,
        },
      };

      // 2) Emit socket
      socket.emit("send_message", {
        roomId: currentRoom._id,
        content,
        fromUserId: me?._id,
        fromUserName: me?.username,
        createdAt: new Date().toISOString(),
      });

      // 3) Hi·ªÉn th·ªã ngay
      setMessages((prev) => [...prev, myMsg]);
      setText("");
      scrollToBottom();
    } catch (err) {
      console.error("‚ùå L·ªói g·ª≠i tin nh·∫Øn:", err);
    }
  };

  // ========= UI =========
  const renderBubble = (m) => {
    const mine = isMine(m);
    return (
      <div
        key={m._id}
        className={`d-flex mb-2 ${mine ? "justify-content-end" : "justify-content-start"}`}
      >
        <div style={{ maxWidth: "72%" }}>
          {!mine && (
            <div className="mb-1">
              <small className="text-muted">
                {m.sender?.username} ¬∑ {new Date(m.createdAt).toLocaleString()}
              </small>
            </div>
          )}

          <div
            className={`p-2 rounded-3 ${mine ? "bg-primary text-white" : "bg-light"}`}
            style={{ display: "inline-block" }}
          >
            {m.content}
          </div>

          {mine && (
            <div className="mt-1 text-end">
              <small className="text-muted">{new Date(m.createdAt).toLocaleString()}</small>
            </div>
          )}
        </div>
      </div>
    );
  };

  const privateTitle = (room) => {
    const others = (room?.participants || []).filter((p) => p._id !== me?._id);
    return others.map((p) => p.username).join(", ");
  };

  return (
    <div className="container-fluid mt-3">
      <div className="row">
        {/* Sidebar */}
        <div className="col-3">
          <SidebarMenu role="user" />
          <div className="card mt-3">
            <div className="card-header">
              <strong>Chat</strong>
            </div>
            <div className="card-body p-2">
              <div className="form-check form-switch mb-2">
                <input
                  className="form-check-input"
                  type="checkbox"
                  checked={chatOutsideDept}
                  onChange={() => setChatOutsideDept((v) => !v)}
                  id="switchOutside"
                />
                <label htmlFor="switchOutside">Chat ngo√†i ph√≤ng ban</label>
              </div>

              {/* Ph√≤ng nh√≥m theo ph√≤ng ban */}
              {deptRoom && (
                <div className="mb-3">
                  <div className="d-flex justify-content-between align-items-center">
                    <strong>üë• {deptRoom.name}</strong>
                    <button
                      className="btn btn-sm btn-outline-primary"
                      onClick={() => openRoom(deptRoom)}
                    >
                      M·ªü
                    </button>
                  </div>
                </div>
              )}

              {/* Danh s√°ch nh√¢n vi√™n */}
              <hr />
              <small className="text-muted">
                Nh√¢n vi√™n {chatOutsideDept ? "to√†n c√¥ng ty" : "trong ph√≤ng ban"}
              </small>
              <div
                className="list-group mt-1"
                style={{ maxHeight: 320, overflowY: "auto" }}
              >
                {employees.length === 0 ? (
                  <div className="text-muted small px-2">
                    Kh√¥ng c√≥ nh√¢n vi√™n n√†o
                  </div>
                ) : (
                  employees
                    .filter((e) => e.userId !== me?._id)
                    .filter((e) =>
                      chatOutsideDept ? true : e.department === myDepartment
                    )
                    .map((e) => (
                      <button
                        key={e._id}
                        className="list-group-item list-group-item-action"
                        onClick={() => startPrivateWith(e.userId)}
                      >
                        {e.name || "Ch∆∞a ƒë·∫∑t t√™n"}{" "}
                        <span className="text-muted">({e.username})</span>
                      </button>
                    ))
                )}
              </div>

              {/* Private rooms g·∫ßn ƒë√¢y */}
              <hr className="my-2" />
              <small className="text-muted">ƒêo·∫°n chat g·∫ßn ƒë√¢y</small>
              <div
                className="list-group mt-1"
                style={{ maxHeight: 200, overflowY: "auto" }}
              >
                {privateRooms.map((r) => (
                  <button
                    key={r._id}
                    className="list-group-item list-group-item-action"
                    onClick={() => openRoom(r)}
                  >
                    {r.type === "group" ? r.name : privateTitle(r)}
                  </button>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* Khung chat */}
        <div className="col-9">
          <div className="card">
            <div className="card-header d-flex align-items-center justify-content-between">
              <div>
                {currentRoom
                  ? currentRoom.type === "group"
                    ? `üë• ${currentRoom.name}`
                    : `üí¨ ${privateTitle(currentRoom)}`
                  : "Ch·ªçn 1 ƒëo·∫°n h·ªôi tho·∫°i"}
              </div>
              <button
                className="btn btn-sm btn-outline-secondary"
                onClick={() => currentRoom && loadMessages(currentRoom)}
                disabled={!currentRoom}
                title="L√†m m·ªõi"
              >
                ‚ü≥
              </button>
            </div>

            <div className="card-body" style={{ height: 520, overflowY: "auto" }}>
              {messages.map(renderBubble)}
              <div ref={bottomRef} />
            </div>

            <div className="card-footer">
              <form onSubmit={sendMessage} className="d-flex gap-2">
                <input
                  className="form-control"
                  placeholder="Nh·∫≠p tin nh·∫Øn..."
                  value={text}
                  onChange={(e) => setText(e.target.value)}
                  disabled={!currentRoom}
                />
                <button
                  className="btn btn-primary"
                  disabled={!currentRoom || !text.trim()}
                >
                  G·ª≠i
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
