import React, { useState } from "react";
import api from "../api/axios";
import Layout from "../components/Layout";

export default function Attendance() {
  const [employeeId, setEmployeeId] = useState("");
  const [msg, setMsg] = useState("");

  const checkIn = async () => {
    await api.post("/attendance/checkin", { employeeId });
    setMsg("Check-in thành công!");
  };
  const checkOut = async () => {
    await api.post("/attendance/checkout", { employeeId });
    setMsg("Check-out thành công!");
  };

  return (
    <Layout title="Chấm công">
      <input className="form-control mb-2" placeholder="EmployeeId" value={employeeId} onChange={e=>setEmployeeId(e.target.value)}/>
      <button className="btn btn-primary me-2" onClick={checkIn}>Check-in</button>
      <button className="btn btn-warning" onClick={checkOut}>Check-out</button>
      <p className="mt-2">{msg}</p>
    </Layout>
  );
}
