// frontend/src/pages/user/ProfileUpdate.js
import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

export default function ProfileUpdate() {
  const [profile, setProfile] = useState(null);
  const [name, setName] = useState("");
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [loading, setLoading] = useState(false);

  // üß© L·∫•y th√¥ng tin hi·ªán t·∫°i
  const loadProfile = async () => {
    try {
      const res = await api.get("/employees/me"); // ‚úÖ route ch√≠nh x√°c
      setProfile(res.data);
      setName(res.data.name || "");
    } catch (err) {
      console.error("‚ùå L·ªói t·∫£i h·ªì s∆°:", err);
      alert("Kh√¥ng th·ªÉ t·∫£i th√¥ng tin c√° nh√¢n!");
    }
  };

  useEffect(() => {
    loadProfile();
  }, []);

  // üíæ C·∫≠p nh·∫≠t t√™n hi·ªÉn th·ªã
  const saveName = async (e) => {
    e.preventDefault();
    if (!name.trim()) return alert("T√™n hi·ªÉn th·ªã kh√¥ng ƒë∆∞·ª£c ƒë·ªÉ tr·ªëng!");
    setLoading(true);
    try {
      await api.put("/employees/profile", { name });
      alert("‚úÖ C·∫≠p nh·∫≠t t√™n hi·ªÉn th·ªã th√†nh c√¥ng!");
      await loadProfile();
    } catch (err) {
      console.error("‚ùå L·ªói c·∫≠p nh·∫≠t:", err);
      alert(err.response?.data?.error || "Kh√¥ng th·ªÉ c·∫≠p nh·∫≠t th√¥ng tin!");
    } finally {
      setLoading(false);
    }
  };

  // üîê ƒê·ªïi m·∫≠t kh·∫©u
  const changePassword = async (e) => {
    e.preventDefault();
    if (!oldPassword || !newPassword) return alert("Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß m·∫≠t kh·∫©u!");
    setLoading(true);
    try {
      await api.put("/employees/change-password", { oldPassword, newPassword });
      alert("‚úÖ ƒê·ªïi m·∫≠t kh·∫©u th√†nh c√¥ng!");
      setOldPassword("");
      setNewPassword("");
    } catch (err) {
      console.error("‚ùå L·ªói ƒë·ªïi m·∫≠t kh·∫©u:", err);
      alert(err.response?.data?.error || "Kh√¥ng th·ªÉ ƒë·ªïi m·∫≠t kh·∫©u!");
    } finally {
      setLoading(false);
    }
  };

  if (!profile) return <div className="p-3 text-center">ƒêang t·∫£i th√¥ng tin...</div>;

  return (
    <div className="container-fluid mt-3">
      <div className="row">
        {/* Sidebar */}
        <div className="col-3">
          <SidebarMenu role="user" />
        </div>

        {/* Main content */}
        <div className="col-9">
          <h3 className="mb-3">üë§ C·∫≠p nh·∫≠t th√¥ng tin c√° nh√¢n</h3>

          {/* Ch·ªânh s·ª≠a t√™n hi·ªÉn th·ªã */}
          <div className="card mb-4">
            <div className="card-header bg-light">
              <strong>ƒê·ªïi t√™n hi·ªÉn th·ªã</strong>
            </div>
            <div className="card-body">
              <form onSubmit={saveName}>
                <div className="mb-3">
                  <label className="form-label">T√™n hi·ªÉn th·ªã</label>
                  <input
                    type="text"
                    className="form-control"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                  />
                </div>
                <button className="btn btn-primary" disabled={loading}>
                  {loading ? "ƒêang l∆∞u..." : "üíæ L∆∞u thay ƒë·ªïi"}
                </button>
              </form>
            </div>
          </div>

          {/* ƒê·ªïi m·∫≠t kh·∫©u */}
          <div className="card mb-4">
            <div className="card-header bg-light">
              <strong>ƒê·ªïi m·∫≠t kh·∫©u</strong>
            </div>
            <div className="card-body">
              <form onSubmit={changePassword}>
                <div className="mb-3">
                  <label className="form-label">M·∫≠t kh·∫©u c≈©</label>
                  <input
                    type="password"
                    className="form-control"
                    value={oldPassword}
                    onChange={(e) => setOldPassword(e.target.value)}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">M·∫≠t kh·∫©u m·ªõi</label>
                  <input
                    type="password"
                    className="form-control"
                    value={newPassword}
                    onChange={(e) => setNewPassword(e.target.value)}
                    required
                  />
                </div>
                <button className="btn btn-warning" disabled={loading}>
                  {loading ? "ƒêang c·∫≠p nh·∫≠t..." : "üîê ƒê·ªïi m·∫≠t kh·∫©u"}
                </button>
              </form>
            </div>
          </div>

          {/* Th√¥ng tin t√†i kho·∫£n */}
          <div className="card">
            <div className="card-header bg-light">
              <strong>Th√¥ng tin t√†i kho·∫£n</strong>
            </div>
            <div className="card-body">
              <p>
                <strong>T√™n ƒëƒÉng nh·∫≠p:</strong> {profile.userId?.username}
              </p>
              <p>
                <strong>Ch·ª©c v·ª•:</strong> {profile.userId?.role}
              </p>
              <p>
                <strong>Ph√≤ng ban:</strong> {profile.department || "‚Äî"}
              </p>
              <p>
                <strong>V·ªã tr√≠:</strong> {profile.position || "‚Äî"}
              </p>
              <p>
                <strong>Ng√†y t·∫°o:</strong>{" "}
                {new Date(profile.createdAt).toLocaleString("vi-VN")}
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
